export default function ChatLog({ history }) {
  if (!history || history.length === 0) {
    return (
      <div
        style={{
          border: "1px solid #888",
          padding: "10px",
          marginBottom: "10px",
          maxHeight: "300px",
          overflowY: "auto",
          backgroundColor: "#111",
          color: "#eee",
        }}
      >
        <p>The adventure is about to begin...</p>
      </div>
    );
  }

  return (
    <div
      style={{
        border: "1px solid #888",
        padding: "10px",
        marginBottom: "10px",
        maxHeight: "300px",
        overflowY: "auto",
        backgroundColor: "#111",
        color: "#eee",
      }}
    >
      {history.map((entry, idx) => (
        <div key={idx} style={{ marginBottom: "8px" }}>
          <strong style={{ color: entry.role === "DM" ? "#f4d03f" : "#5dade2" }}>
            {entry.role === "DM" ? "DM" : "Player"}:
          </strong>{" "}
          {entry.content}
        </div>
      ))}
    </div>
  );
}
