// frontend/src/state/campaignStore.js
import create from "zustand";

export const useCampaignStore = create((set) => ({
  campaignState: {
    tone: "",
    difficulty: "",
    players: [], // array of player sheets
  },
  setCampaignState: (newState) =>
    set((state) => ({ campaignState: { ...state.campaignState, ...newState } })),
  setPlayer: (index, playerData) =>
    set((state) => {
      const players = [...state.campaignState.players];
      players[index] = { ...players[index], ...playerData };
      return { campaignState: { ...state.campaignState, players } };
    }),
  addPlayers: (count) =>
    set((state) => ({
      campaignState: {
        ...state.campaignState,
        players: Array(count)
          .fill(null)
          .map(() => ({
            name: "",
            race: "",
            class: "",
            level: 1,
            currentHp: 10,
            maxHp: 10,
            stats: {},
            personality: "",
            background: "",
          })),
      },
    })),
}));
