// File: frontend/src/state/campaignStore.js
import { create } from "zustand";

export const useCampaignStore = create((set) => ({
  campaign: null, // current campaign state

  // Initialize a new campaign
  startCampaign: (config) =>
    set({
      campaign: {
        ...config,
        lastResponse: "", // last AI message
      },
    }),

  // Update campaign state from DM backend response
  updateFromDM: (dmResponse) =>
    set((state) => ({
      campaign: {
        ...state.campaign,
        ...dmResponse, // merge in new state from backend
        lastResponse: dmResponse.aiResponse || "",
      },
    })),

  // Reset campaign (optional)
  resetCampaign: () => set({ campaign: null }),
}));
