// frontend/src/pages/PlayerList.jsx
import { useNavigate } from "react-router-dom";
import { useCampaignStore } from "../state/campaignStore";

export default function PlayerList() {
  const navigate = useNavigate();
  const players = useCampaignStore((state) => state.players);

  const handleBack = () => {
    navigate("/setup"); // go back to campaign setup
  };

  const handleStartCampaign = () => {
    // You could also do any validation here, e.g., ensure all players have names
    const allSheetsFilled = players.every((p) => p.name && p.class); // example check
    if (!allSheetsFilled) {
      alert("Please fill in all player sheets before starting the campaign!");
      return;
    }
    navigate("/play"); // go to the main game screen
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Player Sheets</h2>

      {players.length === 0 ? (
        <p>No players added yet.</p>
      ) : (
        <ul className="mb-4">
          {players.map((player, idx) => (
            <li
              key={idx}
              className="p-2 border rounded mb-2 cursor-pointer hover:bg-gray-100"
              onClick={() => navigate(`/player/${idx}`)}
            >
              {player.name || `Player ${idx + 1} (click to edit)`}
            </li>
          ))}
        </ul>
      )}

      <div className="flex gap-4">
        <button
          className="bg-gray-400 text-white p-2 rounded hover:bg-gray-500"
          onClick={handleBack}
        >
          Back
        </button>

        <button
          className="bg-green-600 text-white p-2 rounded hover:bg-green-700"
          onClick={handleStartCampaign}
        >
          Start Campaign
        </button>
      </div>
    </div>
  );
}
