import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import DMSetup from "./components/DMSetup";
import PlayerList from "./pages/PlayerList";
import PlayerSheet from "./pages/PlayerSheet";
import Play from "./pages/Play";
import { useState } from "react";

function App() {
  const [campaignConfig, setCampaignConfig] = useState({});
  const [playerSheets, setPlayerSheets] = useState([]);

  const handleStartCampaign = (config) => {
    setCampaignConfig(config);
    setPlayerSheets(Array(config.playerCount).fill({}));
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<DMSetup handleStartCampaign={handleStartCampaign} />} />
        <Route path="/players" element={<PlayerList playerCount={campaignConfig.playerCount || 1} playerSheets={playerSheets} setPlayerSheets={setPlayerSheets} />} />
        <Route path="/player/:playerId" element={<PlayerSheet playerSheets={playerSheets} setPlayerSheets={setPlayerSheets} />} />
        <Route path="/play" element={<Play campaignConfig={campaignConfig} playerSheets={playerSheets} />} />
      </Routes>
    </Router>
  );
}

export default App;
