import { useState, useEffect, useRef } from "react";
import { useCampaignStore } from "../state/campaignStore";
import { playTurn, speakText } from "../services/api";
import ChatLog from "./ChatLog";
import DiceRoller from "./DiceRoller";

export default function GameScreen() {
  const [input, setInput] = useState("");

  const audioQueue = useRef([]);
  const isPlaying = useRef(false);

  const {
    campaign,
    party,
    updateFromDM,
    voiceMode,
    micListening,
    toggleMic,
    addDiceRoll,
  } = useCampaignStore();

  /* ============================
     Speech Recognition (unchanged)
     ============================ */
  useEffect(() => {
    if (!micListening) return;

    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Speech recognition not supported.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";

    recognition.onresult = (e) =>
      setInput(e.results[0][0].transcript);

    recognition.start();
    return () => recognition.stop();
  }, [micListening]);

  /* ============================
     ElevenLabs Voice Style
     ============================ */
  const getVoiceProfile = (description = "") => {
    const desc = description.toLowerCase();

    if (desc.includes("old") || desc.includes("ancient"))
      return "old_wise";
    if (desc.includes("gruff") || desc.includes("orc"))
      return "gruff";
    if (desc.includes("young") || desc.includes("child"))
      return "young";
    if (desc.includes("female"))
      return "female";
    if (desc.includes("evil") || desc.includes("dark"))
      return "dark";

    return "narrator";
  };

  /* ============================
     Queue audio playback
     ============================ */
  const playNextAudio = () => {
    if (isPlaying.current || !audioQueue.current.length) return;

    isPlaying.current = true;
    const audio = audioQueue.current.shift();

    audio.onended = () => {
      isPlaying.current = false;
      playNextAudio();
    };

    audio.play();
  };

  /* ============================
     Speak a single line (ElevenLabs)
     ============================ */
  const speakLine = async (text, description = "") => {
    const cleanText = text
      .replace(/\*\*(.*?)\*\*/g, "$1")
      .replace(/\*(.*?)\*/g, "$1");

    const voice = getVoiceProfile(description);

    try {
      const audioUrl = await speakText({
        text: cleanText,
        voice,
      });

      const audio = new Audio(audioUrl);
      audioQueue.current.push(audio);
      playNextAudio();
    } catch (err) {
      console.error("Voice error:", err);
    }
  };

  /* ============================
     Speak AI response (structured)
     ============================ */
  const speakAIResponse = async (aiResponse) => {
    if (!Array.isArray(aiResponse)) {
      await speakLine(aiResponse);
      return;
    }

    for (const entry of aiResponse) {
      await speakLine(entry.text, entry.description || "");
    }
  };

  /* ============================
     Dice handler
     ============================ */
  const handleDiceRoll = ({ sides, result }) =>
    addDiceRoll({ sides, result });

  /* ============================
     Submit turn
     ============================ */
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const dmResponse = await playTurn({
        message: input,
        campaign,
        party,
      });

      updateFromDM(dmResponse);

      if (voiceMode && dmResponse?.aiResponse) {
        await speakAIResponse(dmResponse.aiResponse);
      }

      setInput("");
    } catch (err) {
      console.error("Turn error:", err);
    }
  };

  if (!campaign) return <p>No campaign started.</p>;

  /* ============================
     UI
     ============================ */
  return (
    <div style={{ padding: "20px" }}>
      <h2>{campaign.theme} Adventure</h2>
      <p>Difficulty: {campaign.difficulty}</p>

      <h3>Party</h3>
      <ul>
        {party.map((c, i) => (
          <li key={i}>
            {c.name} — HP: {c.hp} — {c.status}
          </li>
        ))}
      </ul>

      <DiceRoller onRoll={handleDiceRoll} />

      <h3>Adventure Log</h3>
      <ChatLog history={campaign.history} />

      <form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="What do you do?"
          style={{ width: "300px", marginRight: "5px" }}
        />
        <button type="submit">Submit</button>
        <button
          type="button"
          onClick={toggleMic}
          style={{ marginLeft: "5px" }}
        >
          {micListening ? "Stop Listening" : "Speak"}
        </button>
      </form>
    </div>
  );
}
