import { useCampaignStore } from "../state/campaignStore";
import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

export default function PlayerSheet() {
  const { id } = useParams();
  const navigate = useNavigate();
  const players = useCampaignStore((state) => state.players);
  const setPlayers = useCampaignStore((state) => state.setPlayers);

  const playerIndex = players.findIndex((p) => p.id === Number(id));
  const [playerData, setPlayerData] = useState(players[playerIndex] || {});

  useEffect(() => {
    if (!playerData.id) navigate("/players"); // redirect if player not found
  }, [playerData, navigate]);

  const handleChange = (field, value) => {
    setPlayerData((prev) => ({ ...prev, [field]: value }));
  };

  const handleSave = () => {
    const updatedPlayers = [...players];
    updatedPlayers[playerIndex] = playerData;
    setPlayers(updatedPlayers);
    navigate("/players");
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">{playerData.name}</h2>
      <div className="flex flex-col gap-4">
        <input
          type="text"
          placeholder="Name"
          value={playerData.name || ""}
          onChange={(e) => handleChange("name", e.target.value)}
          className="p-2 border rounded-xl"
        />
        <input
          type="text"
          placeholder="Race"
          value={playerData.race || ""}
          onChange={(e) => handleChange("race", e.target.value)}
          className="p-2 border rounded-xl"
        />
        <input
          type="text"
          placeholder="Class"
          value={playerData.class || ""}
          onChange={(e) => handleChange("class", e.target.value)}
          className="p-2 border rounded-xl"
        />
        <input
          type="number"
          placeholder="Level"
          value={playerData.level || 1}
          onChange={(e) => handleChange("level", Number(e.target.value))}
          className="p-2 border rounded-xl"
        />
        <input
          type="number"
          placeholder="Max HP"
          value={playerData.maxHp || 10}
          onChange={(e) => handleChange("maxHp", Number(e.target.value))}
          className="p-2 border rounded-xl"
        />
      </div>
      <button
        onClick={handleSave}
        className="bg-indigo-600 text-white p-2 rounded-xl mt-4"
      >
        Save
      </button>
    </div>
  );
}
