  caster, 
  availableTargets = [],
  onCastSpell,
  onClose 
}) {
  const [selectedSpell, setSelectedSpell] = useState(null);
  const [selectedTargets, setSelectedTargets] = useState([]);
  const [selectedSlotLevel, setSelectedSlotLevel] = useState(null);

  // Get caster's prepared/known spells
  const knownSpells = caster.spells?.known || [];
  const preparedSpells = caster.spells?.prepared || knownSpells;
  const cantrips = caster.spells?.cantrips || [];

  // Available spell slots
  const spellSlots = caster.spellSlots || { current: [], max: [] };

  const handleToggleTarget = (target) => {
    if (selectedTargets.find(t => t.name === target.name)) {
      setSelectedTargets(selectedTargets.filter(t => t.name !== target.name));
    } else {
      // Check if spell allows multiple targets
      const spell = selectedSpell ? SPELL_DATABASE[selectedSpell] : null;
      if (spell?.range?.includes('ft radius') || spell?.range?.includes('cone') || spell?.range?.includes('line')) {
        // Area spell - allow multiple targets
        setSelectedTargets([...selectedTargets, target]);
      } else {
        // Single target spell
        setSelectedTargets([target]);
      }
    }
  };

  const handleCast = () => {
    if (!selectedSpell) {
      alert('Select a spell!');
      return;
    }

    if (selectedTargets.length === 0) {
      alert('Select at least one target!');
      return;
    }

    const spell = SPELL_DATABASE[selectedSpell];

    // Check if spell slot is needed
    if (spell.level > 0) {
      if (!selectedSlotLevel) {
        alert('Select a spell slot level!');
        return;
      }

      if (spellSlots.current[selectedSlotLevel - 1] <= 0) {
        alert('No spell slots of that level remaining!');
        return;
      }
    }

    onCastSpell({
      spell,
      targets: selectedTargets,
      slotLevel: selectedSlotLevel,
      upcast: selectedSlotLevel > spell.level
    });
  };

  const spellSaveDC = calculateSpellSaveDC(caster);
  const selectedSpellData = selectedSpell ? SPELL_DATABASE[selectedSpell] : null;
  const damagePreview = selectedSpellData ? 
    getSpellDamagePreview(selectedSpellData, caster.level, selectedSlotLevel) : null;

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0,0,0,0.9)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 6000,
      padding: '20px',
      overflow: 'auto'
    }}>
      <div style={{
        backgroundColor: '#1a1a1a',
        borderRadius: '15px',
        border: '3px solid #9C27B0',
        maxWidth: '900px',
        width: '100%',
        maxHeight: '90vh',
        overflow: 'hidden',
        display: 'flex',
        flexDirection: 'column'
      }}>
        {/* Header */}
        <div style={{
          padding: '20px',
          borderBottom: '2px solid #444',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <h2 style={{ margin: 0, color: '#9C27B0' }}>
            ✨ Cast Spell - {caster.name}
          </h2>
          <button
            onClick={onClose}
            style={{
              padding: '8px 16px',
              backgroundColor: '#666',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
          >
            ✕ Close
          </button>
        </div>

        {/* Content */}
        <div style={{
          flex: 1,
          overflowY: 'auto',
          padding: '20px',
          display: 'grid',
          gridTemplateColumns: '1fr 1fr',
          gap: '20px'
        }}>
          {/* Left: Spell Selection */}
          <div>
            <h3 style={{ color: '#9C27B0', marginTop: 0 }}>Select Spell</h3>

            {/* Spell Slots Display */}
            <div style={{
              marginBottom: '15px',
              padding: '12px',
              backgroundColor: '#2a2a2a',
              borderRadius: '8px',
              border: '1px solid #444'
            }}>
              <div style={{ fontSize: '12px', color: '#888', marginBottom: '8px' }}>
                Spell Slots Remaining
              </div>
              <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
                {spellSlots.current.map((current, i) => {
                  if (spellSlots.max[i] === 0) return null;
                  return (
                    <div key={i} style={{
                      padding: '4px 10px',
                      backgroundColor: current > 0 ? '#9C27B0' : '#444',
                      borderRadius: '12px',
                      fontSize: '12px',
                      fontWeight: 'bold'
                    }}>
                      Lvl {i + 1}: {current}/{spellSlots.max[i]}
                    </div>
                  );
                })}
              </div>
            </div>

            {/* Cantrips */}
            {cantrips.length > 0 && (
              <>
                <h4 style={{ color: '#9C27B0', fontSize: '14px' }}>Cantrips</h4>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '8px', marginBottom: '15px' }}>
                  {cantrips.map(spellId => {
                    const spell = SPELL_DATABASE[spellId];
                    if (!spell) return null;

                    return (
                      <button
                        key={spellId}
                        onClick={() => {
                          setSelectedSpell(spellId);
                          setSelectedSlotLevel(0);
                        }}
                        style={{
                          padding: '10px',
                          backgroundColor: selectedSpell === spellId ? '#9C27B0' : '#2a2a2a',
                          color: 'white',
                          border: selectedSpell === spellId ? '2px solid #fff' : '1px solid #444',
                          borderRadius: '8px',
                          cursor: 'pointer',
                          textAlign: 'left',
                          fontSize: '14px'
                        }}
                      >
                        <div style={{ fontWeight: 'bold' }}>{spell.name}</div>
                        <div style={{ fontSize: '11px', color: '#aaa' }}>
                          {spell.school} • {spell.castingTime}
                        </div>
                      </button>
                    );
                  })}
                </div>
              </>
            )}

            {/* Leveled Spells */}
            {preparedSpells.length > 0 && (
              <>
                <h4 style={{ color: '#9C27B0', fontSize: '14px' }}>Prepared Spells</h4>
                <div style={{ 
                  display: 'flex', 
                  flexDirection: 'column', 
                  gap: '8px',
                  maxHeight: '300px',
                  overflowY: 'auto'
                }}>
                  {preparedSpells.map(spellId => {
                    const spell = SPELL_DATABASE[spellId];
                    if (!spell) return null;

                    const hasSlots = spellSlots.current.slice(spell.level - 1).some(s => s > 0);

                    return (
                      <button
                        key={spellId}
                        onClick={() => {
                          setSelectedSpell(spellId);
                          setSelectedSlotLevel(spell.level);
                        }}
                        disabled={!hasSlots}
                        style={{
                          padding: '10px',
                          backgroundColor: selectedSpell === spellId ? '#9C27B0' : '#2a2a2a',
                          color: hasSlots ? 'white' : '#666',
                          border: selectedSpell === spellId ? '2px solid #fff' : '1px solid #444',
                          borderRadius: '8px',
                          cursor: hasSlots ? 'pointer' : 'not-allowed',
                          textAlign: 'left',
                          fontSize: '14px',
                          opacity: hasSlots ? 1 : 0.5
                        }}
                      >
                        <div style={{ fontWeight: 'bold' }}>
                          {spell.name}
                          <span style={{ 
                            marginLeft: '8px', 
                            fontSize: '11px',
                            backgroundColor: '#444',
                            padding: '2px 6px',
                            borderRadius: '4px'
                          }}>
                            Lvl {spell.level}
                          </span>
                        </div>
                        <div style={{ fontSize: '11px', color: '#aaa' }}>
                          {spell.school} • {spell.castingTime}
                        </div>
                      </button>
                    );
                  })}
                </div>
              </>
            )}
          </div>

          {/* Right: Target Selection & Spell Info */}
          <div>
            <h3 style={{ color: '#9C27B0', marginTop: 0 }}>Select Targets</h3>

            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '8px',
              marginBottom: '20px'
            }}>
              {availableTargets.map(target => (
                <button
                  key={target.id || target.name}
                  onClick={() => handleToggleTarget(target)}
                  disabled={!selectedSpell}
                  style={{
                    padding: '12px',
                    backgroundColor: selectedTargets.find(t => t.name === target.name) ? '#d32f2f' : '#2a2a2a',
                    color: 'white',
                    border: selectedTargets.find(t => t.name === target.name) ? '2px solid #ffd700' : '1px solid #444',
                    borderRadius: '8px',
                    cursor: selectedSpell ? 'pointer' : 'not-allowed',
                    textAlign: 'left',
                    opacity: selectedSpell ? 1 : 0.5
                  }}
                >
                  <div style={{ fontWeight: 'bold' }}>{target.name}</div>
                  <div style={{ fontSize: '12px', color: '#aaa' }}>
                    HP: {target.hp}/{target.maxHp} • AC: {target.ac || target.armorClass}
                  </div>
                </button>
              ))}
            </div>

            {/* Spell Details */}
            {selectedSpellData && (
              <div style={{
                padding: '15px',
                backgroundColor: '#2a2a2a',
                borderRadius: '8px',
                border: '2px solid #9C27B0'
              }}>
                <h4 style={{ margin: '0 0 10px 0', color: '#9C27B0' }}>
                  {selectedSpellData.name}
                </h4>

                <div style={{ fontSize: '13px', color: '#ccc', marginBottom: '10px' }}>
                  {selectedSpellData.description}
                </div>

                <div style={{ fontSize: '12px', color: '#888' }}>
                  <div><strong>Level:</strong> {selectedSpellData.level}</div>
                  <div><strong>School:</strong> {selectedSpellData.school}</div>
                  <div><strong>Casting Time:</strong> {selectedSpellData.castingTime}</div>
                  <div><strong>Range:</strong> {selectedSpellData.range}</div>
                  <div><strong>Duration:</strong> {selectedSpellData.duration?.value} {selectedSpellData.duration?.type}</div>
                  {selectedSpellData.concentration && (
                    <div style={{ color: '#ff9800' }}>
                      <strong>⚠️ Requires Concentration</strong>
                    </div>
                  )}
                </div>

                {damagePreview && (
                  <div style={{ 
                    marginTop: '12px', 
                    paddingTop: '12px', 
                    borderTop: '1px solid #444',
                    color: '#ffd700'
                  }}>
                    <div><strong>Damage:</strong> {damagePreview.formula} {damagePreview.damageType}</div>
                    <div><strong>Attack Type:</strong> {damagePreview.attackType}</div>
                    {selectedSpellData.save && (
                      <div><strong>Save DC:</strong> {spellSaveDC} {selectedSpellData.save}</div>
                    )}
                  </div>
                )}

                {/* Upcast Option */}
                {selectedSpellData.level > 0 && selectedSpellData.damagePerLevel && (
                  <div style={{ marginTop: '12px' }}>
                    <label style={{ display: 'block', marginBottom: '8px', color: '#9C27B0' }}>
                      Cast at Level:
                    </label>
                    <select
                      value={selectedSlotLevel || selectedSpellData.level}
                      onChange={(e) => setSelectedSlotLevel(parseInt(e.target.value))}
                      style={{
                        width: '100%',
                        padding: '8px',
                        backgroundColor: '#1a1a1a',
                        color: '#eee',
                        border: '1px solid #444',
                        borderRadius: '5px'
                      }}
                    >
                      {spellSlots.current.map((slots, i) => {
                        if (i + 1 < selectedSpellData.level || slots === 0) return null;
                        return (
                          <option key={i} value={i + 1}>
                            Level {i + 1} ({slots} slots remaining)
                          </option>
                        );
                      })}
                    </select>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Footer */}
        <div style={{
          padding: '20px',
          borderTop: '2px solid #444',
          display: 'flex',
          justifyContent: 'space-between'
        }}>
          <button
            onClick={onClose}
            style={{
              padding: '12px 24px',
              backgroundColor: '#666',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              fontSize: '16px'
            }}
          >
            Cancel
          </button>

          <button
            onClick={handleCast}
            disabled={!selectedSpell || selectedTargets.length === 0}
            style={{
              padding: '12px 24px',
              backgroundColor: (selectedSpell && selectedTargets.length > 0) ? '#9C27B0' : '#444',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              cursor: (selectedSpell && selectedTargets.length > 0) ? 'pointer' : 'not-allowed',
              fontSize: '16px',
              fontWeight: 'bold',
              opacity: (selectedSpell && selectedTargets.length > 0) ? 1 : 0.5
            }}
          >
            ✨ Cast Spell
          </button>
        </div>
      </div>
    </div>
  );
}
