import { useState, useEffect } from "react";
import { useCampaignStore } from "../state/campaignStore";
import { playTurn } from "../services/api";
import ChatLog from "./ChatLog";
import DiceRoller from "./DiceRoller";

// Define some "voice styles" for common archetypes
const voiceStyles = {
  default: { name: "Google UK English Male", rate: 0.95, pitch: 0.9 },
  witch: { name: "Google UK English Female", rate: 1.0, pitch: 1.2 },
  oldMan: { name: "Google UK English Male", rate: 0.85, pitch: 0.7 },
  merchant: { name: "Google UK English Male", rate: 1.05, pitch: 1.0 },
  child: { name: "Google UK English Female", rate: 1.2, pitch: 1.3 },
};

// Map keywords in AI narration to voice archetypes
const npcKeywordMap = [
  { keyword: /witch|crone|hag/i, style: "witch" },
  { keyword: /old man|elder/i, style: "oldMan" },
  { keyword: /merchant|trader/i, style: "merchant" },
  { keyword: /child|kid/i, style: "child" },
];

export default function GameScreen() {
  const [input, setInput] = useState("");

  const {
    campaign,
    party,
    updateFromDM,
    voiceMode,
    micListening,
    toggleMic,
    addDiceRoll,
  } = useCampaignStore();

  /* ============================
     ðŸŽ¤ SPEECH RECOGNITION
     ============================ */
  useEffect(() => {
    if (!micListening) return;

    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition || typeof SpeechRecognition !== "function") {
      console.warn("Speech recognition not supported.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = false;
    recognition.lang = "en-US";

    recognition.onresult = (event) => {
      if (!event.results || !event.results[0]) return;
      const spoken = event.results[0][0].transcript;
      setInput(spoken);
    };

    recognition.onerror = (err) => console.error("Speech recognition error:", err);

    recognition.start();
    return () => recognition.stop();
  }, [micListening]);

  /* ============================
     ðŸ”Š TTS with dynamic NPC voices
     ============================ */
  const speak = (text) => {
    // Remove markdown for speech
    const cleanText = text.replace(/\*\*(.*?)\*\*/g, "$1").replace(/\*(.*?)\*/g, "$1");

    // Determine voice style based on keywords
    let style = voiceStyles.default;
    for (const entry of npcKeywordMap) {
      if (entry.keyword.test(cleanText)) {
        style = voiceStyles[entry.style] || style;
        break;
      }
    }

    const voices = speechSynthesis.getVoices();
    const selectedVoice = voices.find((v) => v.name === style.name);
    const utterance = new SpeechSynthesisUtterance(cleanText);
    utterance.rate = style.rate;
    utterance.pitch = style.pitch;
    if (selectedVoice) utterance.voice = selectedVoice;

    speechSynthesis.cancel();
    speechSynthesis.speak(utterance);

    // Simple sound effect triggers
    if (/door opens/i.test(cleanText)) new Audio("/sounds/door-creak.mp3").play();
    if (/magic/i.test(cleanText)) new Audio("/sounds/magic.wav").play();
    if (/sword|clash/i.test(cleanText)) new Audio("/sounds/sword.mp3").play();
  };

  /* ============================
     ðŸŽ² DICE HANDLER
     ============================ */
  const handleDiceRoll = ({ sides, result }) => {
    addDiceRoll({ sides, result });
  };

  /* ============================
     ðŸ§  SUBMIT TURN
     ============================ */
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const dmResponse = await playTurn({ message: input, campaign, party });
      updateFromDM(dmResponse);

      if (voiceMode && dmResponse?.aiResponse) speak(dmResponse.aiResponse);

      setInput("");
    } catch (err) {
      console.error("Turn error:", err);
    }
  };

  if (!campaign) return <p>No campaign started.</p>;

  /* ============================
     ðŸ§™ UI
     ============================ */
  return (
    <div style={{ padding: "20px" }}>
      <h2>{campaign.theme} Adventure</h2>
      <p>Difficulty: {campaign.difficulty}</p>

      <h3>Party</h3>
      <ul>
        {party.map((c, i) => (
          <li key={i}>
            {c.name} â€” HP: {c.hp} â€” {c.status}
          </li>
        ))}
      </ul>

      <DiceRoller onRoll={handleDiceRoll} />

      <h3>Adventure Log</h3>
      <ChatLog history={campaign.history} />

      <form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="What do you do?"
          style={{ width: "300px", marginRight: "5px" }}
        />
        <button type="submit">Submit</button>
        <button type="button" onClick={toggleMic} style={{ marginLeft: "5px" }}>
          {micListening ? "Stop Listening" : "Speak"}
        </button>
      </form>
    </div>
  );
}
