import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

export default function PlayerSheet({ playerSheets, setPlayerSheets }) {
  const { playerId } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState(playerSheets[playerId] || {
    name: "",
    race: "",
    class: "",
    level: 1,
    maxHp: 10,
    currentHp: 10,
    stats: { str: 10, dex: 10, con: 10, int: 10, wis: 10, cha: 10 },
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleStatChange = (stat, value) => {
    setForm({ ...form, stats: { ...form.stats, [stat]: parseInt(value) } });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedSheets = [...playerSheets];
    updatedSheets[playerId] = form;
    setPlayerSheets(updatedSheets);
    navigate("/players"); // go back to list
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Player {parseInt(playerId) + 1} Character Sheet</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-3">
        <input
          name="name"
          placeholder="Character Name"
          value={form.name}
          onChange={handleChange}
          className="p-2 border rounded-xl"
        />
        <input
          name="race"
          placeholder="Race"
          value={form.race}
          onChange={handleChange}
          className="p-2 border rounded-xl"
        />
        <input
          name="class"
          placeholder="Class"
          value={form.class}
          onChange={handleChange}
          className="p-2 border rounded-xl"
        />
        <input
          name="level"
          type="number"
          placeholder="Level"
          value={form.level}
          onChange={handleChange}
          className="p-2 border rounded-xl"
        />
        <input
          name="maxHp"
          type="number"
          placeholder="Max HP"
          value={form.maxHp}
          onChange={handleChange}
          className="p-2 border rounded-xl"
        />
        <input
          name="currentHp"
          type="number"
          placeholder="Current HP"
          value={form.currentHp}
          onChange={handleChange}
          className="p-2 border rounded-xl"
        />
        <div className="grid grid-cols-3 gap-2">
          {Object.keys(form.stats).map((stat) => (
            <input
              key={stat}
              type="number"
              value={form.stats[stat]}
              onChange={(e) => handleStatChange(stat, e.target.value)}
              placeholder={stat.toUpperCase()}
              className="p-2 border rounded-xl"
            />
          ))}
        </div>
        <button type="submit" className="bg-indigo-600 text-white p-2 rounded-xl hover:bg-indigo-700">
          Save Sheet
        </button>
      </form>
    </div>
  );
}
