import { create } from "zustand";

export const useCampaignStore = create((set) => ({
  campaign: {
    systemPrompt: "",
    gameState: {},
    history: [],
  },

  startCampaign: (systemPrompt, players) =>
    set({
      campaign: {
        systemPrompt,
        gameState: { players },
        history: [],
      },
    }),

  updateFromDM: (dmResponse) =>
    set((state) => ({
      campaign: {
        ...state.campaign,
        history: [
          ...state.campaign.history,
          { role: "assistant", content: dmResponse.narration },
        ],
      },
    })),
}));
