export default function ChatLog({ history }) {
  return (
    <div
      style={{
        border: "1px solid #888",
        padding: "10px",
        marginBottom: "10px",
        maxHeight: "300px",
        overflowY: "auto",
        backgroundColor: "#222", // darker but visible
        color: "#fff",           // white text for contrast
        fontFamily: "monospace", // easier to read
      }}
    >
      {(!history || history.length === 0) && (
        <p style={{ color: "#aaa" }}>The adventure is about to begin...</p>
      )}
      {history &&
        history.map((entry, idx) => (
          <div key={idx} style={{ marginBottom: "8px" }}>
            <strong style={{ color: entry.role === "user" ? "#4af" : "#fa4" }}>
              {entry.role === "user" ? "Player" : "DM"}:
            </strong>{" "}
            <span>{entry.content}</span>
          </div>
        ))}
    </div>
  );
}
