import { useState, useEffect } from "react";
import { useCampaignStore } from "../state/campaignStore";
import { playTurn } from "../services/api";
import ChatLog from "./ChatLog";
import DiceRoller from "./DiceRoller";
import CombatPanel from "./CombatPanel";

export default function GameScreen() {
  const [input, setInput] = useState("");

  const {
    campaign,
    party,
    updateFromDM,
    voiceMode,
    micListening,
    toggleMic,
    addDiceRoll,
    startCombat,
  } = useCampaignStore();

  useEffect(() => {
    if (!micListening) return;

    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition || typeof SpeechRecognition !== "function") {
      alert("Speech recognition not supported.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.onresult = (e) =>
      setInput(e.results[0][0].transcript);
    recognition.start();

    return () => recognition.stop();
  }, [micListening]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const dmResponse = await playTurn({
      message: input,
      campaign,
      party,
    });

    updateFromDM(dmResponse);
    setInput("");
  };

  if (!campaign) return <p>No campaign started.</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>{campaign.theme} Adventure</h2>

      <button
        onClick={() =>
          startCombat({
            enemies: [
              {
                id: "goblin-1",
                name: "Goblin",
                hp: 12,
                maxHp: 12,
                alive: true,
              },
            ],
          })
        }
      >
        ⚔️ Start Combat
      </button>

      <DiceRoller onRoll={addDiceRoll} />
      <ChatLog history={campaign.history} />
      <CombatPanel />

      <form onSubmit={handleSubmit}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="What do you do?"
        />
        <button type="submit">Submit</button>
        <button type="button" onClick={toggleMic}>
          Speak
        </button>
      </form>
    </div>
  );
}
