import { useState } from "react";
import { useCampaignStore } from "../state/campaignStore";
import { useNavigate } from "react-router-dom";

export default function DMSetup() {
  const [tone, setTone] = useState("dark fantasy, eldritch horror");
  const [difficulty, setDifficulty] = useState("standard");
  const [playerCount, setPlayerCount] = useState(1);

  const setPlayers = useCampaignStore((state) => state.setPlayers);
  const setCampaignConfig = useCampaignStore((state) => state.setCampaignConfig);

  const navigate = useNavigate();

  const handleStartCampaign = (e) => {
    e.preventDefault();

    // create empty players array
    const players = Array.from({ length: playerCount }, (_, i) => ({
      id: i + 1,
      name: `Player ${i + 1}`,
      // you can add more default stats here
      race: "",
      class: "",
      level: 1,
      maxHp: 10,
      currentHp: 10,
      stats: {}
    }));

    setPlayers(players);
    setCampaignConfig({ tone, difficulty });

    // navigate to player list
    navigate("/players");
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Start Your Campaign</h2>
      <form onSubmit={handleStartCampaign} className="flex flex-col gap-4">
        <div>
          <label>Campaign Tone / Theme:</label>
          <input
            type="text"
            value={tone}
            onChange={(e) => setTone(e.target.value)}
            className="w-full p-2 border rounded-xl"
          />
        </div>
        <div>
          <label>Difficulty:</label>
          <select
            value={difficulty}
            onChange={(e) => setDifficulty(e.target.value)}
            className="w-full p-2 border rounded-xl"
          >
            <option value="casual">Casual</option>
            <option value="standard">Standard</option>
            <option value="deadly">Deadly</option>
          </select>
        </div>
        <div>
          <label>Number of Players:</label>
          <input
            type="number"
            min="1"
            max="6"
            value={playerCount}
            onChange={(e) => setPlayerCount(Number(e.target.value))}
            className="w-full p-2 border rounded-xl"
          />
        </div>
        <button type="submit" className="bg-indigo-600 text-white p-2 rounded-xl">
          Generate DM
        </button>
      </form>
    </div>
  );
}
