import { useState } from "react";
import { useCampaignStore } from "../state/campaignStore";

export default function DMSetup({ onStart }) {
  const startCampaign = useCampaignStore((state) => state.startCampaign);
  const addCharacter = useCampaignStore((state) => state.addCharacter);
  const [theme, setTheme] = useState("Fantasy");
  const [difficulty, setDifficulty] = useState("Normal");
  const [maxPlayers, setMaxPlayers] = useState(4);
  const [characterName, setCharacterName] = useState("");

  const handleAddCharacter = () => {
    if (!characterName.trim()) return;
    addCharacter({ name: characterName, hp: 100, status: "Healthy" });
    setCharacterName("");
  };

  const handleStart = () => {
    startCampaign({ theme, difficulty, maxPlayers });
    onStart(); // Switch to Play page
  };

  const party = useCampaignStore.getState().party;

  return (
    <div style={{ padding: "20px" }}>
      <h2>DM Setup</h2>

      <label>Theme:</label>
      <input value={theme} onChange={(e) => setTheme(e.target.value)} />

      <label>Difficulty:</label>
      <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
        <option>Easy</option>
        <option>Normal</option>
        <option>Hard</option>
      </select>

      <label>Max Players:</label>
      <input
        type="number"
        value={maxPlayers}
        onChange={(e) => setMaxPlayers(Number(e.target.value))}
      />

      <h3>Add Player Characters</h3>
      <input
        placeholder="Character Name"
        value={characterName}
        onChange={(e) => setCharacterName(e.target.value)}
      />
      <button onClick={handleAddCharacter}>Add Character</button>

      <h4>Party:</h4>
      <ul>
        {party.map((c, idx) => (
          <li key={idx}>{c.name}</li>
        ))}
      </ul>

      <button onClick={handleStart}>Start Campaign</button>
    </div>
  );
}
