// frontend/src/state/campaignStore.js
import { create } from "zustand";

export const useCampaignStore = create((set) => ({
  campaign: null,
  party: [],
  lastResponse: "",
  voiceMode: true,
  micListening: false,

  // Set a new campaign
  setCampaign: (campaign) =>
    set({
      campaign: { ...campaign }, // new object reference for React
      party: campaign.party || [],
      lastResponse:
        campaign.history?.slice(-1)[0]?.content || "The adventure begins...",
    }),

  // Update from DM response
  updateFromDM: ({ aiResponse, campaignState }) =>
    set({
      campaign: { ...campaignState }, // new reference triggers re-render
      lastResponse: aiResponse,
      party: campaignState.party || [],
    }),

  toggleMic: () => set((state) => ({ micListening: !state.micListening })),
  toggleVoice: () => set((state) => ({ voiceMode: !state.voiceMode })),
}));
