import { useState } from "react";

export default function DMSetup({ handleStartCampaign }) {
  const [tone, setTone] = useState("dark fantasy, eldritch horror");
  const [difficulty, setDifficulty] = useState("standard");
  const [playerCount, setPlayerCount] = useState(1);
  const [players, setPlayers] = useState([""]);

  const updatePlayer = (index, value) => {
    const updated = [...players];
    updated[index] = value;
    setPlayers(updated);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    handleStartCampaign({
      tone,
      difficulty,
      players: players.filter(p => p.trim() !== "")
    });
  };

  const handlePlayerCountChange = (count) => {
    setPlayerCount(count);
    setPlayers(Array.from({ length: count }, () => ""));
  };

  return (
    <div className="p-6 max-w-xl mx-auto bg-zinc-900 text-white rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4">Start Your Campaign</h2>

      <form onSubmit={handleSubmit} className="flex flex-col gap-4">

        <div>
          <label className="block mb-1">Campaign Tone / Theme</label>
          <input
            value={tone}
            onChange={(e) => setTone(e.target.value)}
            className="w-full p-2 rounded-xl bg-zinc-800 border border-zinc-700"
          />
        </div>

        <div>
          <label className="block mb-1">Difficulty</label>
          <select
            value={difficulty}
            onChange={(e) => setDifficulty(e.target.value)}
            className="w-full p-2 rounded-xl bg-zinc-800 border border-zinc-700"
          >
            <option value="casual">Casual</option>
            <option value="standard">Standard</option>
            <option value="deadly">Deadly</option>
          </select>
        </div>

        <div>
          <label className="block mb-1">Number of Players</label>
          <input
            type="number"
            min={1}
            max={6}
            value={playerCount}
            onChange={(e) => handlePlayerCountChange(Number(e.target.value))}
            className="w-full p-2 rounded-xl bg-zinc-800 border border-zinc-700"
          />
        </div>

        {players.map((player, i) => (
          <div key={i}>
            <label className="block mb-1">Player {i + 1} Sheet</label>
            <textarea
              value={player}
              onChange={(e) => updatePlayer(i, e.target.value)}
              placeholder="Class, race, backstory, notable traits..."
              className="w-full p-2 rounded-xl bg-zinc-800 border border-zinc-700"
              rows={3}
            />
          </div>
        ))}

        <button className="bg-indigo-600 hover:bg-indigo-700 p-3 rounded-xl font-semibold">
          Generate DM
        </button>
      </form>
    </div>
  );
}
