import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function DMSetup({ handleStartCampaign }) {
  const [tone, setTone] = useState("dark fantasy, eldritch horror");
  const [difficulty, setDifficulty] = useState("standard");
  const [playerCount, setPlayerCount] = useState(1);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Save campaign config in parent state or context
    handleStartCampaign({ tone, difficulty, playerCount });
    // Navigate to player list page
    navigate("/players");
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Start Your Campaign</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <div>
          <label className="block mb-1">Campaign Tone / Theme:</label>
          <input
            type="text"
            value={tone}
            onChange={(e) => setTone(e.target.value)}
            className="w-full p-2 border rounded-xl"
          />
        </div>
        <div>
          <label className="block mb-1">Difficulty:</label>
          <select
            value={difficulty}
            onChange={(e) => setDifficulty(e.target.value)}
            className="w-full p-2 border rounded-xl"
          >
            <option value="casual">Casual</option>
            <option value="standard">Standard</option>
            <option value="deadly">Deadly</option>
          </select>
        </div>
        <div>
          <label className="block mb-1">Number of Players:</label>
          <input
            type="number"
            min={1}
            max={6}
            value={playerCount}
            onChange={(e) => setPlayerCount(parseInt(e.target.value))}
            className="w-full p-2 border rounded-xl"
          />
        </div>
        <button
          type="submit"
          className="bg-indigo-600 text-white p-2 rounded-xl hover:bg-indigo-700"
        >
          Next: Setup Player Sheets
        </button>
      </form>
    </div>
  );
}
