import { useCampaignStore } from "../state/campaignStore";
import { startCampaign } from "../services/api";

export default function DMSetup() {
  const setCampaign = useCampaignStore((state) => state.setCampaign);

  const handleStart = async () => {
    try {
      console.log("Starting campaign...");
      const result = await startCampaign({
        theme: "Dark Fantasy",
        difficulty: "Normal",
        party: [{ name: "Nyx Ellisar" }],
      });

      console.log("Backend returned:", result);

      // ✅ Store campaign so GameScreen sees it
      setCampaign(result.campaignState);
    } catch (err) {
      console.error("Failed to start campaign:", err);
    }
  };

  return (
    <div>
      <button onClick={handleStart}>Start Campaign</button>
    </div>
  );
}
