import { useState, useEffect } from "react";
import { useCampaignStore } from "../state/campaignStore";
import { playTurn } from "../services/api";
import ChatLog from "./ChatLog";
import DiceRoller from "./DiceRoller";

// Define NPC voices
const npcVoices = {
  "Nyx Ellisar": "Google UK English Male",
  "The Villager": "Google UK English Female",
  "Dark Wizard": "Google UK English Male",
};

const soundEffects = {
  "door opens": "/sounds/door-creak.mp3",
  "thunder": "/sounds/thunder.mp3",
  "magic": "/sounds/magic.wav",
  "sword clash": "/sounds/sword.mp3",
};

export default function GameScreen() {
  const [input, setInput] = useState("");

  const {
    campaign,
    party,
    updateFromDM,
    voiceMode,
    micListening,
    toggleMic,
    addDiceRoll,
  } = useCampaignStore();

  /* ============================
     ðŸŽ¤ SPEECH RECOGNITION
     ============================ */
  useEffect(() => {
    if (!micListening) return;

    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition || typeof SpeechRecognition !== "function") {
      console.warn("Speech recognition not supported.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = false;
    recognition.lang = "en-US";

    recognition.onresult = (event) => {
      if (!event.results || !event.results[0]) return;
      const spoken = event.results[0][0].transcript;
      setInput(spoken);
    };

    recognition.onerror = (err) => {
      console.error("Speech recognition error:", err);
    };

    recognition.start();
    return () => recognition.stop();
  }, [micListening]);

  /* ============================
     ðŸ”Š DM & NPC VOICE
     ============================ */
  const speak = (text, npcName = null) => {
    // Clean Markdown for TTS
    const cleanText = text
      .replace(/\*\*(.*?)\*\*/g, "$1") // bold
      .replace(/\*(.*?)\*/g, "$1"); // italics

    const utterance = new SpeechSynthesisUtterance(cleanText);
    utterance.rate = 0.95;
    utterance.pitch = 0.9;

    // Select voice (DM or NPC)
    const voices = speechSynthesis.getVoices();
    const voiceName = npcName ? npcVoices[npcName] : "Google UK English Male";
    const selected = voices.find((v) => v.name === voiceName);
    if (selected) utterance.voice = selected;

    speechSynthesis.cancel();
    speechSynthesis.speak(utterance);

    // Play sound effects if keywords exist
    Object.entries(soundEffects).forEach(([keyword, file]) => {
      if (cleanText.toLowerCase().includes(keyword)) {
        const audio = new Audio(file);
        audio.volume = 0.5;
        audio.play();
      }
    });
  };

  /* ============================
     ðŸŽ² DICE HANDLER
     ============================ */
  const handleDiceRoll = ({ sides, result }) => {
    addDiceRoll({ sides, result });
  };

  /* ============================
     ðŸ§  SUBMIT TURN
     ============================ */
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const dmResponse = await playTurn({ message: input, campaign, party });
      updateFromDM(dmResponse);

      if (voiceMode && dmResponse?.aiResponse) {
        // Check if DM tagged an NPC in text (simple heuristic)
        const npcMatch = Object.keys(npcVoices).find((n) =>
          dmResponse.aiResponse.includes(n)
        );
        speak(dmResponse.aiResponse, npcMatch || null);
      }

      setInput("");
    } catch (err) {
      console.error("Turn error:", err);
    }
  };

  if (!campaign) return <p>No campaign started.</p>;

  /* ============================
     ðŸ§™ UI
     ============================ */
  return (
    <div style={{ padding: "20px" }}>
      <h2>{campaign.theme} Adventure</h2>
      <p>Difficulty: {campaign.difficulty}</p>

      <h3>Party</h3>
      <ul>
        {party.map((c, i) => (
          <li key={i}>
            {c.name} â€” HP: {c.hp} â€” {c.status}
          </li>
        ))}
      </ul>

      {/* ðŸŽ² Dice UI */}
      <DiceRoller onRoll={handleDiceRoll} />

      <h3>Adventure Log</h3>
      <ChatLog history={campaign.history} />

      <form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="What do you do?"
          style={{ width: "300px", marginRight: "5px" }}
        />
        <button type="submit">Submit</button>
        <button
          type="button"
          onClick={toggleMic}
          style={{ marginLeft: "5px" }}
        >
          {micListening ? "Stop Listening" : "Speak"}
        </button>
      </form>
    </div>
  );
}
