import { create } from "zustand";

export const useCampaignStore = create((set, get) => ({
  campaign: null,
  party: [],
  lastResponse: "",
  voiceMode: false,
  micListening: false,

  // Update from DM turn
  updateFromDM: (dmResponse) =>
    set((state) => {
      const newHistory = [...(state.campaign?.history || []), { role: "assistant", content: dmResponse.aiResponse }];
      return {
        campaign: { ...state.campaign, history: newHistory },
        lastResponse: dmResponse.aiResponse,
      };
    }),

  // Set entire campaign (used when starting)
  setCampaign: (campaign) => set({ campaign, party: campaign.party || [] }),

  toggleMic: () => set((state) => ({ micListening: !state.micListening })),
}));
