import { useState, useEffect, useRef } from "react";
import { useCampaignStore } from "../state/campaignStore";
import { playTurn } from "../services/api";
import ChatLog from "./ChatLog";

export default function GameScreen() {
  const [input, setInput] = useState("");
  const {
    campaign,
    party,
    updateFromDM,
    voiceMode,
    micListening,
    toggleMic,
  } = useCampaignStore();

  const chatEndRef = useRef(null);

  if (!campaign) return <p>No campaign started.</p>;

  // Scroll to bottom whenever history updates
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [campaign.history]);

  // Mic listening
  useEffect(() => {
    if (!micListening) return;

    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = false;

    recognition.onresult = (event) => {
      const spoken = event.results[event.results.length - 1][0].transcript;
      setInput(spoken);
    };

    recognition.start();

    return () => recognition.stop();
  }, [micListening]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const dmResponse = await playTurn({ message: input, campaign, party });
      updateFromDM(dmResponse);

      if (voiceMode && dmResponse.aiResponse) {
        const utterance = new SpeechSynthesisUtterance(dmResponse.aiResponse);
        speechSynthesis.speak(utterance);
      }

      setInput("");
    } catch (err) {
      console.error("Error during turn:", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>{campaign.theme} Adventure</h2>
      <p>Difficulty: {campaign.difficulty}</p>

      <h3>Party</h3>
      <ul>
        {party.map((c, idx) => (
          <li
            key={idx}
            style={{
              color: c.hp <= 20 ? "red" : "white", // highlight danger
              fontWeight: c.hp <= 20 ? "bold" : "normal",
            }}
          >
            {c.name} — HP: {c.hp} — Status: {c.status}
          </li>
        ))}
      </ul>

      <h3>Adventure Log</h3>
      <ChatLog history={campaign.history} />
      <div ref={chatEndRef} /> {/* scroll target */}

      <form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="What do you do?"
          style={{ width: "300px" }}
        />
        <button type="submit" style={{ marginLeft: "5px" }}>
          Submit
        </button>
        <button type="button" onClick={toggleMic} style={{ marginLeft: "5px" }}>
          {micListening ? "Stop Listening" : "Listen"}
        </button>
      </form>
    </div>
  );
}
