import { useEffect, useRef } from "react";

export default function ChatLog({ history }) {
  const bottomRef = useRef(null);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [history]);

  return (
    <div
      style={{
        border: "1px solid #444",
        padding: "10px",
        marginBottom: "10px",
        maxHeight: "300px",
        overflowY: "auto",
        backgroundColor: "#0d0d0d",
        color: "#eee",
      }}
    >
      {history.map((entry, idx) => {
        const danger =
          entry.content.toLowerCase().includes("danger") ||
          entry.content.toLowerCase().includes("death") ||
          entry.content.toLowerCase().includes("blood");

        return (
          <div
            key={idx}
            style={{
              marginBottom: "8px",
              color: danger ? "#ff5555" : "#eee",
            }}
          >
            <strong>{entry.role === "user" ? "Player" : "DM"}:</strong>{" "}
            {entry.content}
          </div>
        );
      })}
      <div ref={bottomRef} />
    </div>
  );
}
