// frontend/src/components/SaveLoadModal.jsx
import { useState, useEffect } from "react";
import { getAllSaves, saveCampaign, loadCampaign, deleteSave, exportSave, importSave } from "../services/saveLoadService";

export default function SaveLoadModal({ isOpen, onClose, mode, campaign, party, onLoad }) {
  const [saves, setSaves] = useState([]);
  const [saveName, setSaveName] = useState("");
  const [selectedSlot, setSelectedSlot] = useState(null);

  useEffect(() => {
    if (isOpen) {
      refreshSaves();
    }
  }, [isOpen]);

  const refreshSaves = () => {
    setSaves(getAllSaves());
  };

  const handleSave = () => {
    if (!saveName.trim()) {
      alert("Please enter a save name");
      return;
    }

    const slotId = selectedSlot || `save-${Date.now()}`;
    const success = saveCampaign(slotId, campaign, party, saveName);

    if (success) {
      alert("Campaign saved!");
      refreshSaves();
      onClose();
    }
  };

  const handleLoad = (save) => {
    if (window.confirm(`Load "${save.saveName}"? Current progress will be lost if not saved.`)) {
      const data = loadCampaign(save.id);
      if (data) {
        onLoad(data.campaign, data.party);
        onClose();
      }
    }
  };

  const handleDelete = (saveId, saveName) => {
    if (window.confirm(`Delete "${saveName}"? This cannot be undone.`)) {
      deleteSave(saveId);
      refreshSaves();
    }
  };

  const handleExport = (saveId) => {
    exportSave(saveId);
  };

  const handleImport = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const slotId = `imported-${Date.now()}`;
    importSave(file, slotId)
      .then(() => {
        alert("Save imported successfully!");
        refreshSaves();
      })
      .catch((err) => {
        alert("Failed to import save: " + err.message);
      });
  };

  if (!isOpen) return null;

  return (
    <div className="modal-backdrop">
      <div className="card-elevated" style={{
        maxWidth: '700px',
        width: '90%',
        maxHeight: '80vh',
        overflowY: 'auto'
      }}>
        <h2 className="text-primary" style={{ marginTop: 0 }}>
          {mode === "save" ? "üíæ Save Campaign" : "üìÇ Load Campaign"}
        </h2>

        {mode === "save" && (
          <div className="mb-lg">
            <input
              type="text"
              value={saveName}
              onChange={(e) => setSaveName(e.target.value)}
              placeholder="Enter save name..."
              className="input mb-md"
            />
            <button onClick={handleSave} className="btn btn-success">
              üíæ Save Now
            </button>
          </div>
        )}

        <h3 className="text-primary mb-md">Saved Campaigns</h3>

        {saves.length === 0 ? (
          <p className="text-muted" style={{ fontStyle: "italic" }}>
            No saved campaigns yet.
          </p>
        ) : (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
            {saves.map((save) => (
              <div
                key={save.id}
                className={selectedSlot === save.id ? 'card-active' : 'card'}
                onClick={() => mode === "save" && setSelectedSlot(save.id)}
                style={{ cursor: mode === "save" ? "pointer" : "default" }}
              >
                <div className="flex flex-between" style={{ alignItems: 'start' }}>
                  <div style={{ flex: 1 }}>
                    <h4 className="text-primary" style={{ margin: "0 0 8px 0" }}>
                      {save.saveName}
                    </h4>
                    <p className="text-secondary" style={{ margin: "4px 0", fontSize: "14px" }}>
                      {save.thumbnail}
                    </p>
                    <p className="text-muted" style={{ margin: "8px 0 0 0", fontSize: "12px" }}>
                      üíæ Saved: {new Date(save.lastSaved).toLocaleString()}
                      {" ‚Ä¢ "}
                      ‚è±Ô∏è Play Time: {save.playTime}
                    </p>
                  </div>

                  <div className="flex gap-sm" style={{ flexDirection: "column" }}>
                    {mode === "load" && (
                      <button
                        onClick={() => handleLoad(save)}
                        className="btn btn-success btn-sm"
                      >
                        üìÇ Load
                      </button>
                    )}
                    <button
                      onClick={() => handleExport(save.id)}
                      className="btn btn-info btn-sm"
                    >
                      üì§ Export
                    </button>
                    <button
                      onClick={() => handleDelete(save.id, save.saveName)}
                      className="btn btn-danger btn-sm"
                    >
                      üóëÔ∏è Delete
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        <div className="divider" />

        <label className="btn" style={{ 
          backgroundColor: '#9C27B0',
          cursor: 'pointer',
          display: 'inline-block',
          marginBottom: '10px'
        }}>
          üì• Import Save File
          <input
            type="file"
            accept=".json"
            onChange={handleImport}
            style={{ display: "none" }}
          />
        </label>

        <button onClick={onClose} className="btn btn-ghost" style={{ width: '100%' }}>
          Close
        </button>
      </div>
    </div>
  );
}
