// File: frontend/src/components/GameScreen.jsx
import { useState } from "react";
import { playTurn } from "../services/api";
import { useCampaignStore } from "../state/campaignStore";

export default function GameScreen() {
  const [input, setInput] = useState("");
  const { campaign, startCampaign, updateFromDM } = useCampaignStore();

  // Start a new campaign
  const handleStartCampaign = () => {
    startCampaign({
      name: "Epic AI Dungeon",
      difficulty: "Hard",
      startedAt: new Date().toISOString(),
      state: {}, // initial game state for backend
    });
  };

  // Submit player action
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const dmResponse = await playTurn({
        message: input,
        campaign,
      });

      updateFromDM(dmResponse);
      setInput("");
    } catch (err) {
      console.error("Error during turn:", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      {!campaign ? (
        <button onClick={handleStartCampaign}>Start New Campaign</button>
      ) : (
        <>
          <h2>{campaign.name}</h2>
          <p>
            <strong>Difficulty:</strong> {campaign.difficulty}
          </p>

          <form onSubmit={handleSubmit}>
            <input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="What do you do?"
              style={{ width: "300px", marginRight: "10px" }}
            />
            <button type="submit">Submit</button>
          </form>

          <div style={{ marginTop: "20px" }}>
            <h3>Last AI Response:</h3>
            <p>{campaign.lastResponse}</p>
          </div>
        </>
      )}
    </div>
  );
}
