import { useState, useEffect, useRef } from "react";
import { useCampaignStore } from "../state/campaignStore";
import { playTurn, speakText } from "../services/api";
import ChatLog from "./ChatLog";
import DiceRoller from "./DiceRoller";

export default function GameScreen() {
  const [input, setInput] = useState("");

  const audioQueue = useRef([]);
  const isPlaying = useRef(false);

  const {
    campaign,
    party,
    updateFromDM,
    voiceMode,
    micListening,
    toggleMic,
    addDiceRoll,
  } = useCampaignStore();

  /* ============================
     Speech Recognition (unchanged)
     ============================ */
  useEffect(() => {
    if (!micListening) return;

    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) return;

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";

    recognition.onresult = (e) =>
      setInput(e.results[0][0].transcript);

    recognition.start();
    return () => recognition.stop();
  }, [micListening]);

  /* ============================
     Voice selection from AI text
     ============================ */
  const getVoiceFromDescription = (description = "") => {
    const d = description.toLowerCase();
    if (d.includes("old") || d.includes("ancient")) return "old_wise";
    if (d.includes("gruff") || d.includes("orc")) return "gruff";
    if (d.includes("young") || d.includes("child")) return "young";
    if (d.includes("evil") || d.includes("dark")) return "dark";
    if (d.includes("female")) return "female";
    return "narrator";
  };

  /* ============================
     Audio queue system
     ============================ */
  const playNext = () => {
    if (isPlaying.current || !audioQueue.current.length) return;

    isPlaying.current = true;
    const audio = audioQueue.current.shift();

    audio.onended = () => {
      isPlaying.current = false;
      playNext();
    };

    audio.play();
  };

  const speakLine = async (text, description = "") => {
    const cleanText = text
      .replace(/\*\*(.*?)\*\*/g, "$1")
      .replace(/\*(.*?)\*/g, "$1");

    const voice = getVoiceFromDescription(description);

    try {
      const audioSrc = await speakText({ text: cleanText, voice });
      const audio = new Audio(audioSrc);
      audioQueue.current.push(audio);
      playNext();
    } catch (err) {
      console.error("Voice error:", err);
    }
  };

  const speakAIResponse = async (aiResponse) => {
    if (!Array.isArray(aiResponse)) {
      await speakLine(aiResponse);
      return;
    }

    for (const line of aiResponse) {
      await speakLine(line.text, line.description);
    }
  };

  /* ============================
     Submit turn
     ============================ */
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const dmResponse = await playTurn({
        message: input,
        campaign,
        party,
      });

      console.log("DM RESPONSE RAW:", dmResponse); // ðŸ”¥ ADD THIS

      updateFromDM(dmResponse);

      if (voiceMode && dmResponse?.aiResponse) {
        await speakAIResponse(dmResponse.aiResponse);
      }

      setInput("");
    } catch (err) {
      console.error("Turn error:", err);
    }
  };

  if (!campaign) return <p>No campaign started.</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>{campaign.theme} Adventure</h2>
      <p>Difficulty: {campaign.difficulty}</p>

      <h3>Party</h3>
      <ul>
        {party.map((c, i) => (
          <li key={i}>
            {c.name} â€” HP: {c.hp} â€” {c.status}
          </li>
        ))}
      </ul>

      <DiceRoller onRoll={addDiceRoll} />

      <h3>Adventure Log</h3>
      <ChatLog history={campaign.history} />

      <form onSubmit={handleSubmit}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="What do you do?"
        />
        <button type="submit">Submit</button>
        <button type="button" onClick={toggleMic}>
          {micListening ? "Stop Listening" : "Speak"}
        </button>
      </form>
    </div>
  );
}
