import { useState, useEffect, useRef } from "react";
import { useCampaignStore } from "../state/campaignStore";
import { playTurn } from "../services/api";
import { generateVoice } from "../services/voiceApi";
import { generateSoundEffects } from "../services/soundEffectsApi";
import { parseNarrative } from "../utils/narrativeParser";
import { autoSave } from "../services/saveLoadService";
import ChatLog from "./ChatLog";
import DiceRoller from "./DiceRoller";
import SaveLoadModal from "./SaveLoadModal";

export default function GameScreen() {
  const [input, setInput] = useState("");
  const [isGeneratingVoice, setIsGeneratingVoice] = useState(false);
  const audioRef = useRef(null);
  const audioQueue = useRef([]);
  const isPlaying = useRef(false);
  const recognitionRef = useRef(null); // Store recognition instance
  const silenceTimeoutRef = useRef(null);

  const campaign = useCampaignStore((state) => state.campaign);
  const party = useCampaignStore((state) => state.party);
  const updateFromDM = useCampaignStore((state) => state.updateFromDM);
  const setCampaign = useCampaignStore((state) => state.setCampaign);
  const voiceMode = useCampaignStore((state) => state.voiceMode);
  const soundEffectsEnabled = useCampaignStore((state) => state.soundEffectsEnabled || false);
  const micListening = useCampaignStore((state) => state.micListening);
  const toggleMic = useCampaignStore((state) => state.toggleMic);
  const addDiceRoll = useCampaignStore((state) => state.addDiceRoll);
  const toggleVoice = useCampaignStore((state) => state.toggleVoice);
  const toggleSoundEffects = useCampaignStore((state) => state.toggleSoundEffects || (() => {}));

  /* ============================
     Speech Recognition
     ============================ */
  useEffect(() => {
    if (!micListening) {
      console.log("üîá Mic is off, cleaning up if needed");
      // Stop recognition if it's running
      if (recognitionRef.current) {
        try {
          recognitionRef.current.stop();
        } catch (e) {
          console.log("Recognition already stopped");
        }
        recognitionRef.current = null;
      }
      if (silenceTimeoutRef.current) {
        clearTimeout(silenceTimeoutRef.current);
        silenceTimeoutRef.current = null;
      }
      return;
    }

    console.log("üé§ Mic listening state changed to: true");

    const startRecognition = async () => {
      // Request mic permission first
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        console.log("‚úÖ Microphone permission granted!");
        stream.getTracks().forEach(track => track.stop());
      } catch (err) {
        console.error("‚ùå Microphone permission denied:", err);
        alert("Please allow microphone access when prompted by your browser.");
        toggleMic();
        return;
      }

      const SpeechRecognition =
        window.SpeechRecognition || window.webkitSpeechRecognition;

      if (!SpeechRecognition) {
        console.error("‚ùå Speech recognition not supported");
        alert("Speech recognition is not supported in this browser. Try Chrome or Edge.");
        toggleMic();
        return;
      }

      try {
        const recognition = new SpeechRecognition();
        recognitionRef.current = recognition;

        recognition.continuous = true;
        recognition.lang = "en-US";
        recognition.interimResults = true;
        recognition.maxAlternatives = 1;

        let finalTranscript = '';

        recognition.onstart = () => {
          console.log("‚úÖ Microphone ACTUALLY started listening!");
          finalTranscript = '';
        };

        recognition.onaudiostart = () => {
          console.log("üîä Audio capture started");
        };

        recognition.onsoundstart = () => {
          console.log("üîä Sound detected!");
        };

        recognition.onspeechstart = () => {
          console.log("üó£Ô∏è Speech started!");
          if (silenceTimeoutRef.current) {
            clearTimeout(silenceTimeoutRef.current);
            silenceTimeoutRef.current = null;
          }
        };

        recognition.onresult = (event) => {
          console.log("üìù Got result!");

          let interimTranscript = '';

          for (let i = event.resultIndex; i < event.results.length; i++) {
            const transcript = event.results[i][0].transcript;

            if (event.results[i].isFinal) {
              console.log(`‚úÖ Final: "${transcript}"`);
              finalTranscript += transcript + ' ';
            } else {
              console.log(`‚è≥ Interim: "${transcript}"`);
              interimTranscript += transcript;
            }
          }

          setInput((finalTranscript + interimTranscript).trim());
        };

        recognition.onspeechend = () => {
          console.log("üõë Speech ended (will continue listening)");
        };

        recognition.onerror = (err) => {
          console.error("‚ùå Recognition error:", err.error);

          if (err.error === 'not-allowed') {
            alert("Microphone blocked! Check browser settings.");
          } else if (err.error === 'no-speech') {
            console.log("‚ö†Ô∏è No speech - continuing...");
          }
        };

        recognition.onend = () => {
          console.log("üé§ Recognition ended");

          // Don't auto-restart - just notify user
          console.log("Click the STOP button to turn off the microphone");
        };

        console.log("üé§ Starting speech recognition NOW...");
        recognition.start();

      } catch (err) {
        console.error("‚ùå Failed to create recognition:", err);
        alert("Failed to start: " + err.message);
        toggleMic();
      }
    };

    startRecognition();
  }, [micListening]); // Removed toggleMic from dependencies

  /* ============================
     Audio Queue System
     ============================ */
  const playAudioQueue = () => {
    if (isPlaying.current || audioQueue.current.length === 0) return;

    isPlaying.current = true;
    const audioItem = audioQueue.current.shift();

    if (!audioRef.current) {
      audioRef.current = new Audio();
    }

    audioRef.current.src = audioItem.audio;
    audioRef.current.volume = audioItem.volume || 1.0;

    audioRef.current.onended = () => {
      console.log(`‚úÖ Finished playing ${audioItem.type}`);
      isPlaying.current = false;
      playAudioQueue(); // Play next in queue
    };

    audioRef.current.onerror = (err) => {
      console.error("‚ùå Audio playback error:", err);
      isPlaying.current = false;
      playAudioQueue();
    };

    console.log(`üîä Playing ${audioItem.type}...`);
    audioRef.current.play().catch(err => {
      console.error("‚ùå Failed to play audio:", err);
      isPlaying.current = false;
      playAudioQueue();
    });
  };

  /* ============================
     Play Background Sound Effects (simultaneous with narration)
     ============================ */
  const playBackgroundSound = (audioData, category) => {
    try {
      const audio = new Audio(audioData);
      audio.volume = 0.3; // Quiet background sound
      audio.loop = false;

      console.log(`üéµ Playing background sound: ${category}`);

      audio.play().catch(err => {
        console.error("‚ùå Failed to play background sound:", err);
      });

      // Auto-cleanup after it finishes
      audio.onended = () => {
        console.log(`‚úÖ Background sound finished: ${category}`);
      };
    } catch (err) {
      console.error("‚ùå Background sound error:", err);
    }
  };

  /* ============================
     Generate and Play Sound Effects
     ============================ */
  const playSoundEffects = async (text) => {
    if (!soundEffectsEnabled) {
      console.log("üîá Sound effects disabled by user");
      return;
    }

    try {
      console.log("üîä Requesting sound effects for text...");
      const soundEffects = await generateSoundEffects(text);

      console.log(`üì¶ Received ${soundEffects.length} sound effects from backend`);

      if (soundEffects.length === 0) {
        console.log("üîá No sound effects generated for this text");
        return;
      }

      // Play sound effects immediately in the background (not queued)
      soundEffects.forEach(sfx => {
        console.log(`üéµ Playing background sound: ${sfx.category}`);
        playBackgroundSound(sfx.audio, sfx.category);
      });

    } catch (err) {
      console.error("‚ùå Failed to play sound effects:", err);
    }
  };

  /* ============================
     ElevenLabs Voice Generation
     ============================ */
  const speakWithElevenLabs = async (text, voice = "narrator") => {
    if (!voiceMode) {
      console.log("üîá Voice mode is OFF - skipping narration");
      return;
    }

    try {
      setIsGeneratingVoice(true);
      console.log(`üéôÔ∏è Generating ${voice} voice with ElevenLabs...`);

      // Clean text of markdown
      const cleanText = text
        .replace(/\*\*(.*?)\*\*/g, "$1")
        .replace(/\*(.*?)\*/g, "$1")
        .replace(/#{1,6}\s/g, "");

      // Generate narration
      const audioData = await generateVoice({ text: cleanText, voice });

      // Add narration to queue
      audioQueue.current.push({
        audio: audioData,
        type: `narration_${voice}`,
        volume: 1.0
      });

      // If nothing is playing, start the queue
      if (!isPlaying.current) {
        playAudioQueue();
      }

      setIsGeneratingVoice(false);
    } catch (err) {
      console.error("‚ùå Failed to generate voice:", err);
      setIsGeneratingVoice(false);
      // Fallback to browser TTS
      console.log("‚ö†Ô∏è Falling back to browser TTS");
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.speak(utterance);
    }
  };

  const speakAIResponse = async (aiResponse) => {
    console.log("üéôÔ∏è Voice mode:", voiceMode);
    console.log("üìù AI Response:", aiResponse);

    if (!voiceMode) {
      console.log("üîá Voice mode is OFF - skipping narration");
      return;
    }

    // Parse the entire response for dialogue first
    const fullText = typeof aiResponse === 'string' ? aiResponse : 
                     Array.isArray(aiResponse) ? aiResponse.map(e => e.text).join(' ') : '';

    // Generate sound effects ONCE for the entire narration (plays in background)
    if (soundEffectsEnabled && fullText) {
      playSoundEffects(fullText);
    }

    // Handle array of structured responses
    if (Array.isArray(aiResponse)) {
      console.log("üìö Speaking array of", aiResponse.length, "entries");
      for (const entry of aiResponse) {
        const voice = entry.description?.toLowerCase().includes("dark") ? "dark" :
                     entry.description?.toLowerCase().includes("old") ? "old_wise" :
                     entry.description?.toLowerCase().includes("gruff") ? "gruff" :
                     "narrator";
        await speakWithElevenLabs(entry.text, voice);
      }
      return;
    }

    // Handle plain text - parse for dialogue
    if (typeof aiResponse === 'string' && aiResponse.trim()) {
      console.log("üìñ Parsing narrative for dialogue...");
      const segments = parseNarrative(aiResponse);
      console.log(`üé≠ Found ${segments.length} segments`);

      // Speak each segment with appropriate voice
      for (const segment of segments) {
        console.log(`  üó£Ô∏è ${segment.type} (${segment.voice}): "${segment.text.substring(0, 40)}..."`);
        await speakWithElevenLabs(segment.text, segment.voice);
      }
      return;
    }

    console.warn("‚ö†Ô∏è Unknown AI response format");
  };

  /* ============================
     Dice handler
     ============================ */
  const handleDiceRoll = ({ sides, result }) => {
    console.log("üé≤ Rolled d" + sides + ":", result);
    addDiceRoll({ sides, result });
  };

  /* ============================
     Submit player turn
     ============================ */
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    console.log("üéÆ Submitting player action:", input);

    try {
      const dmResponse = await playTurn({ message: input, campaign, party });
      console.log("üì® DM Response received:", dmResponse);

      updateFromDM(dmResponse);

      // Auto-save after each turn
      autoSave(dmResponse.campaignState, party);

      // Trigger ElevenLabs narration with sound effects
      if (dmResponse?.aiResponse) {
        console.log("üé§ Attempting to speak response...");
        await speakAIResponse(dmResponse.aiResponse);
      } else {
        console.warn("‚ö†Ô∏è No aiResponse in DM response");
      }

      setInput("");
    } catch (err) {
      console.error("‚ùå Turn error:", err);
    }
  };

  const handleLoadCampaign = (loadedCampaign, loadedParty) => {
    setCampaign({
      ...loadedCampaign,
      party: loadedParty
    });
    alert("Campaign loaded successfully!");
  };

  if (!campaign) return <p>No campaign started.</p>;

  /* ============================
     UI
     ============================ */
  return (
    <div style={{ padding: "20px" }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "20px" }}>
        <div>
          <h2 style={{ margin: 0 }}>{campaign.theme} Adventure</h2>
          <p style={{ margin: "5px 0" }}>Difficulty: {campaign.difficulty}</p>
        </div>

        {/* Save/Load Buttons */}
        <div style={{ display: "flex", gap: "10px" }}>
          <button
            onClick={() => setShowSaveModal(true)}
            style={{
              padding: "10px 20px",
              backgroundColor: "#4CAF50",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              fontWeight: "bold",
              fontSize: "14px"
            }}
          >
            üíæ Save
          </button>
          <button
            onClick={() => setShowLoadModal(true)}
            style={{
              padding: "10px 20px",
              backgroundColor: "#2196F3",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              fontWeight: "bold",
              fontSize: "14px"
            }}
          >
            üìÇ Load
          </button>
        </div>
      </div>

      {/* Audio Controls */}
      <div style={{ marginBottom: "15px", padding: "10px", border: "1px solid #ccc", borderRadius: "5px" }}>
        <h4 style={{ marginTop: 0 }}>Audio Settings</h4>

        <label style={{ display: "block", marginBottom: "5px" }}>
          <input 
            type="checkbox" 
            checked={voiceMode} 
            onChange={toggleVoice}
          />
          {" "}üéôÔ∏è Voice Narration {voiceMode ? "ON" : "OFF"}
        </label>

        <label style={{ display: "block" }}>
          <input 
            type="checkbox" 
            checked={soundEffectsEnabled} 
            onChange={toggleSoundEffects}
          />
          {" "}üîä Sound Effects {soundEffectsEnabled ? "ON" : "OFF"}
        </label>

        {isGeneratingVoice && (
          <div style={{ marginTop: "5px", color: "#888" }}>
            ‚è≥ Generating audio...
          </div>
        )}
      </div>

      <h3>Party</h3>
      <ul>
        {party.map((c, i) => (
          <li key={i}>
            {c.name} ‚Äî HP: {c.hp} ‚Äî {c.status}
          </li>
        ))}
      </ul>

      <DiceRoller onRoll={handleDiceRoll} />

      <h3>Adventure Log</h3>
      <ChatLog history={campaign.history} />

      <form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="What do you do?"
          style={{ 
            width: "300px", 
            marginRight: "5px"
          }}
          disabled={isGeneratingVoice}
        />
        <button type="submit" disabled={isGeneratingVoice}>
          Submit
        </button>
        {/* Speech input is optional - typing works great! */}
        <button 
          type="button" 
          onClick={toggleMic} 
          style={{ 
            marginLeft: "5px",
            backgroundColor: micListening ? "#ff4444" : "#666",
            color: "white",
            opacity: 0.5,
            cursor: "not-allowed"
          }}
          disabled={true}
          title="Speech input is currently disabled - just type your actions!"
        >
          üé§ Voice Input (Disabled)
        </button>
      </form>

      {/* Save/Load Modals */}
      <SaveLoadModal
        isOpen={showSaveModal}
        onClose={() => setShowSaveModal(false)}
        mode="save"
        campaign={campaign}
        party={party}
        onLoad={handleLoadCampaign}
      />

      <SaveLoadModal
        isOpen={showLoadModal}
        onClose={() => setShowLoadModal(false)}
        mode="load"
        campaign={campaign}
        party={party}
        onLoad={handleLoadCampaign}
      />
    </div>
  );
}
