export default function ChatLog({ history }) {
  return (
    <div
      style={{
        border: "1px solid #888",
        padding: "10px",
        marginBottom: "10px",
        maxHeight: "300px",
        overflowY: "auto",
        backgroundColor: "#111",
        color: "#eee",
      }}
    >
      {history?.length === 0 && <p>The adventure is about to begin...</p>}
      {history?.map((entry, idx) => (
        <div
          key={idx}
          style={{
            marginBottom: "8px",
            color: entry.role === "assistant" ? "#9cf" : "#eee",
          }}
        >
          <strong>{entry.role === "user" ? "Player" : "DM"}:</strong>{" "}
          {entry.content}
        </div>
      ))}
    </div>
  );
}
