import { useState, useEffect, useRef } from "react";
import { useCampaignStore } from "../state/campaignStore";
import { playTurn } from "../services/api";
import { generateVoice } from "../services/voiceApi";
import ChatLog from "./ChatLog";
import DiceRoller from "./DiceRoller";

export default function GameScreen() {
  const [input, setInput] = useState("");
  const [isGeneratingVoice, setIsGeneratingVoice] = useState(false);
  const audioRef = useRef(null);
  const audioQueue = useRef([]);
  const isPlaying = useRef(false);

  const {
    campaign,
    party,
    updateFromDM,
    voiceMode,
    micListening,
    toggleMic,
    addDiceRoll,
    toggleVoice,
  } = useCampaignStore();

  /* ============================
     Speech Recognition
     ============================ */
  useEffect(() => {
    if (!micListening) return;

    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition || typeof SpeechRecognition !== "function") {
      console.warn("Speech recognition not supported.");
      alert("Speech recognition is not supported in this browser.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = false;
    recognition.lang = "en-US";

    recognition.onresult = (event) => {
      if (!event.results || !event.results[0]) return;
      const transcript = event.results[0][0].transcript;
      console.log("üé§ Heard:", transcript);
      setInput(transcript);
    };

    recognition.onerror = (err) => console.error("Speech recognition error:", err);

    recognition.start();
    return () => recognition.stop();
  }, [micListening]);

  /* ============================
     ElevenLabs Voice Generation
     ============================ */
  const playAudioQueue = () => {
    if (isPlaying.current || audioQueue.current.length === 0) return;

    isPlaying.current = true;
    const audioData = audioQueue.current.shift();

    if (!audioRef.current) {
      audioRef.current = new Audio();
    }

    audioRef.current.src = audioData;
    audioRef.current.onended = () => {
      console.log("‚úÖ Finished playing audio");
      isPlaying.current = false;
      playAudioQueue(); // Play next in queue
    };

    audioRef.current.onerror = (err) => {
      console.error("‚ùå Audio playback error:", err);
      isPlaying.current = false;
      playAudioQueue();
    };

    console.log("üîä Playing audio...");
    audioRef.current.play().catch(err => {
      console.error("‚ùå Failed to play audio:", err);
      isPlaying.current = false;
      playAudioQueue();
    });
  };

  const speakWithElevenLabs = async (text, voice = "narrator") => {
    if (!voiceMode) {
      console.log("üîá Voice mode is OFF - skipping narration");
      return;
    }

    try {
      setIsGeneratingVoice(true);
      console.log("üéôÔ∏è Generating voice with ElevenLabs...");

      // Clean text of markdown
      const cleanText = text
        .replace(/\*\*(.*?)\*\*/g, "$1")
        .replace(/\*(.*?)\*/g, "$1")
        .replace(/#{1,6}\s/g, "");

      const audioData = await generateVoice({ text: cleanText, voice });

      // Add to queue
      audioQueue.current.push(audioData);
      playAudioQueue();

      setIsGeneratingVoice(false);
    } catch (err) {
      console.error("‚ùå Failed to generate voice:", err);
      setIsGeneratingVoice(false);
      // Fallback to browser TTS
      console.log("‚ö†Ô∏è Falling back to browser TTS");
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.speak(utterance);
    }
  };

  const speakAIResponse = async (aiResponse) => {
    console.log("üéôÔ∏è Voice mode:", voiceMode);
    console.log("üìù AI Response:", aiResponse);

    if (!voiceMode) {
      console.log("üîá Voice mode is OFF - skipping narration");
      return;
    }

    // Handle array of structured responses
    if (Array.isArray(aiResponse)) {
      console.log("üìö Speaking array of", aiResponse.length, "entries");
      for (const entry of aiResponse) {
        const voice = entry.description?.toLowerCase().includes("dark") ? "dark" :
                     entry.description?.toLowerCase().includes("old") ? "old_wise" :
                     entry.description?.toLowerCase().includes("gruff") ? "gruff" :
                     "narrator";
        await speakWithElevenLabs(entry.text, voice);
      }
      return;
    }

    // Handle plain text
    if (typeof aiResponse === 'string' && aiResponse.trim()) {
      console.log("üìñ Speaking plain text");
      await speakWithElevenLabs(aiResponse, "narrator");
      return;
    }

    console.warn("‚ö†Ô∏è Unknown AI response format");
  };

  /* ============================
     Dice handler
     ============================ */
  const handleDiceRoll = ({ sides, result }) => {
    console.log("üé≤ Rolled d" + sides + ":", result);
    addDiceRoll({ sides, result });
  };

  /* ============================
     Submit player turn
     ============================ */
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    console.log("üéÆ Submitting player action:", input);

    try {
      const dmResponse = await playTurn({ message: input, campaign, party });
      console.log("üì® DM Response received:", dmResponse);

      updateFromDM(dmResponse);

      // Trigger ElevenLabs narration
      if (dmResponse?.aiResponse) {
        console.log("üé§ Attempting to speak response...");
        await speakAIResponse(dmResponse.aiResponse);
      } else {
        console.warn("‚ö†Ô∏è No aiResponse in DM response");
      }

      setInput("");
    } catch (err) {
      console.error("‚ùå Turn error:", err);
    }
  };

  if (!campaign) return <p>No campaign started.</p>;

  /* ============================
     UI
     ============================ */
  return (
    <div style={{ padding: "20px" }}>
      <h2>{campaign.theme} Adventure</h2>
      <p>Difficulty: {campaign.difficulty}</p>

      {/* Voice Mode Toggle */}
      <div style={{ marginBottom: "10px" }}>
        <label>
          <input 
            type="checkbox" 
            checked={voiceMode} 
            onChange={toggleVoice}
          />
          {" "}üéôÔ∏è ElevenLabs Narration {voiceMode ? "ON" : "OFF"}
        </label>
        {isGeneratingVoice && (
          <span style={{ marginLeft: "10px", color: "#888" }}>
            ‚è≥ Generating voice...
          </span>
        )}
      </div>

      <h3>Party</h3>
      <ul>
        {party.map((c, i) => (
          <li key={i}>
            {c.name} ‚Äî HP: {c.hp} ‚Äî {c.status}
          </li>
        ))}
      </ul>

      <DiceRoller onRoll={handleDiceRoll} />

      <h3>Adventure Log</h3>
      <ChatLog history={campaign.history} />

      <form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="What do you do?"
          style={{ width: "300px", marginRight: "5px" }}
          disabled={isGeneratingVoice}
        />
        <button type="submit" disabled={isGeneratingVoice}>
          Submit
        </button>
        <button 
          type="button" 
          onClick={toggleMic} 
          style={{ marginLeft: "5px" }}
          disabled={isGeneratingVoice}
        >
          {micListening ? "üî¥ Stop Listening" : "üé§ Speak"}
        </button>
      </form>
    </div>
  );
}
