import { useState, useEffect, useRef } from "react";
import { useCampaignStore } from "../state/campaignStore";
import { playTurn } from "../services/api";
import ChatLog from "./ChatLog";
import DiceRoller from "./DiceRoller";

export default function GameScreen() {
  const [input, setInput] = useState("");
  const voicesRef = useRef([]);
  const {
    campaign,
    party,
    updateFromDM,
    voiceMode,
    micListening,
    toggleMic,
    addDiceRoll,
  } = useCampaignStore();

  /* ============================
     Load available voices
     ============================ */
  useEffect(() => {
    const loadVoices = () => {
      voicesRef.current = window.speechSynthesis.getVoices();
    };
    loadVoices();
    window.speechSynthesis.onvoiceschanged = loadVoices;
  }, []);

  /* ============================
     Speech Recognition
     ============================ */
  useEffect(() => {
    if (!micListening) return;

    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition || typeof SpeechRecognition !== "function") {
      console.warn("Speech recognition not supported.");
      alert("Speech recognition is not supported in this browser.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = false;
    recognition.lang = "en-US";

    recognition.onresult = (event) => {
      if (!event.results || !event.results[0]) return;
      setInput(event.results[0][0].transcript);
    };

    recognition.onerror = (err) => console.error("Speech recognition error:", err);

    recognition.start();
    return () => recognition.stop();
  }, [micListening]);

  /* ============================
     Voice styles per NPC
     ============================ */
  const voiceStyles = {
    DM: { rate: 0.95, pitch: 0.9, name: "" },        // default voice
    Crone: { rate: 0.9, pitch: 0.7, name: "Microsoft Zira" },
    Witch: { rate: 0.85, pitch: 0.7, name: "Google UK English Female" },
    Merchant: { rate: 1.0, pitch: 1.0, name: "Google US English Male" },
    Child: { rate: 1.1, pitch: 1.2, name: "Google UK English Female" },
  };

  /* ============================
     Speak a single line
     ============================ */
  const speakLine = (text, styleKey = "DM") => {
    const style = voiceStyles[styleKey] || voiceStyles.DM;

    const cleanText = text.replace(/\*\*(.*?)\*\*/g, "$1").replace(/\*(.*?)\*/g, "$1");

    const u = new SpeechSynthesisUtterance(cleanText);
    u.rate = style.rate;
    u.pitch = style.pitch;

    // Match voice by name if exists
    const selectedVoice = voicesRef.current.find((v) => v.name === style.name);
    if (selectedVoice) u.voice = selectedVoice;

    // Sound effects triggers
    if (/door opens/i.test(cleanText)) new Audio("/sounds/door-creak.mp3").play();
    if (/magic/i.test(cleanText)) new Audio("/sounds/magic.wav").play();
    if (/sword|clash/i.test(cleanText)) new Audio("/sounds/sword.mp3").play();

    speechSynthesis.speak(u);
  };

  /* ============================
     Speak structured AI response
     ============================ */
  const speakAIResponse = (aiResponse) => {
    if (!Array.isArray(aiResponse)) {
      // fallback to single string
      speakLine(aiResponse, "DM");
      return;
    }

    aiResponse.forEach((entry) => {
      const speaker = entry.speaker || "DM";
      speakLine(entry.text, speaker);
    });
  };

  /* ============================
     Dice handler
     ============================ */
  const handleDiceRoll = ({ sides, result }) => addDiceRoll({ sides, result });

  /* ============================
     Submit player turn
     ============================ */
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const dmResponse = await playTurn({ message: input, campaign, party });
      updateFromDM(dmResponse);

      if (voiceMode && dmResponse?.aiResponse) speakAIResponse(dmResponse.aiResponse);

      setInput("");
    } catch (err) {
      console.error("Turn error:", err);
    }
  };

  if (!campaign) return <p>No campaign started.</p>;

  /* ============================
     UI
     ============================ */
  return (
    <div style={{ padding: "20px" }}>
      <h2>{campaign.theme} Adventure</h2>
      <p>Difficulty: {campaign.difficulty}</p>

      <h3>Party</h3>
      <ul>
        {party.map((c, i) => (
          <li key={i}>
            {c.name} — HP: {c.hp} — {c.status}
          </li>
        ))}
      </ul>

      <DiceRoller onRoll={handleDiceRoll} />

      <h3>Adventure Log</h3>
      <ChatLog history={campaign.history} />

      <form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="What do you do?"
          style={{ width: "300px", marginRight: "5px" }}
        />
        <button type="submit">Submit</button>
        <button type="button" onClick={toggleMic} style={{ marginLeft: "5px" }}>
          {micListening ? "Stop Listening" : "Speak"}
        </button>
      </form>
    </div>
  );
}
