import { useState, useEffect } from "react";
import { useCampaignStore } from "../state/campaignStore";
import { playTurn } from "../services/api";

export default function GameScreen() {
  const [input, setInput] = useState("");
  const {
    campaign,
    party,
    lastResponse,
    updateFromDM,
    voiceMode,
    micListening,
    toggleMic,
  } = useCampaignStore();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const dmResponse = await playTurn({ message: input, campaign, party });
      updateFromDM(dmResponse);

      // Voice output
      if (voiceMode && dmResponse.aiResponse) {
        const utterance = new SpeechSynthesisUtterance(dmResponse.aiResponse);
        speechSynthesis.speak(utterance);
      }

      setInput("");
    } catch (err) {
      console.error("Error during turn:", err);
    }
  };

  // Mic input
  useEffect(() => {
    if (!micListening) return;

    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = false;

    recognition.onresult = (event) => {
      const spoken = event.results[event.results.length - 1][0].transcript;
      setInput(spoken);
    };

    recognition.start();
    return () => recognition.stop();
  }, [micListening]);

  if (!campaign) return <p>No campaign started.</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>{campaign.theme || "Adventure"}</h2>
      <p>Difficulty: {campaign.difficulty}</p>

      {/* Party Panel */}
      <div style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px" }}>
        <h3>Party</h3>
        <ul>
          {party.map((c, idx) => (
            <li key={idx}>
              {c.name} — HP: {c.hp ?? "?"} — Status: {c.status ?? "Unknown"}
            </li>
          ))}
        </ul>
      </div>

      {/* Chat Log */}
      <div
        style={{
          border: "1px solid #ccc",
          padding: "10px",
          maxHeight: "300px",
          overflowY: "auto",
          marginBottom: "10px",
        }}
      >
        <h3>Chat Log</h3>
        {campaign.history && campaign.history.length > 0 ? (
          campaign.history.map((msg, idx) => (
            <p key={idx} style={{ margin: "5px 0" }}>
              <strong>{msg.role === "assistant" ? "DM" : "Player"}:</strong>{" "}
              {msg.content}
            </p>
          ))
        ) : (
          <p>{lastResponse || "The adventure begins..."}</p>
        )}
      </div>

      {/* Input */}
      <form onSubmit={handleSubmit} style={{ display: "flex", gap: "10px" }}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="What do you do?"
          style={{ flex: 1 }}
        />
        <button type="submit">Submit</button>
        <button type="button" onClick={toggleMic}>
          {micListening ? "Stop Listening" : "Listen"}
        </button>
      </form>
    </div>
  );
}
