export async function playTurn({ message, campaign }) {
  const res = await fetch("http://localhost:3001/api/turn/play", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      message,
      campaign,
    }),
  });

  if (!res.ok) {
    throw new Error("Failed to play turn");
  }

  return res.json();
}
