// frontend/src/services/api.js

const API_BASE = "http://localhost:3001/api";

/**
 * Start a new campaign
 * @param {string} tone - Campaign tone/theme
 * @param {string} difficulty - Campaign difficulty
 */
export async function startCampaign({ tone, difficulty }) {
  const res = await fetch(`${API_BASE}/campaign/start`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ tone, difficulty })
  });

  if (!res.ok) throw new Error("Failed to start campaign");
  return res.json();
}

/**
 * Play a turn
 * @param {string} playerAction - Player input
 * @param {string} systemPrompt - DM system prompt
 */
export async function playTurn({ playerAction, systemPrompt }) {
  const res = await fetch(`${API_BASE}/turn/play`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ playerAction, systemPrompt })
  });

  if (!res.ok) throw new Error("Failed to play turn");
  return res.json();
}
