import { create } from "zustand";

export const useCampaignStore = create((set) => ({
  campaign: null,
  party: [],
  voiceMode: true,
  micListening: false,
  diceRolls: [],

  startCampaign: (campaign, party) =>
    set({
      campaign,
      party,
    }),

  updateFromDM: ({ campaignState }) =>
    set({
      campaign: campaignState,
    }),

  toggleMic: () =>
    set((state) => ({ micListening: !state.micListening })),

  addDiceRoll: (roll) =>
    set((state) => ({ diceRolls: [...state.diceRolls, roll] })),
}));
