// frontend/src/pages/Play.jsx
import { useCampaignStore } from "../state/campaignStore";

export default function Play() {
  const players = useCampaignStore((state) => state.players) || [];

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">The Adventure Begins</h1>

      {players.length === 0 ? (
        <p className="text-gray-500">
          No players found. Please return to setup.
        </p>
      ) : (
        <div className="space-y-4">
          {players.map((player, index) => (
            <div
              key={index}
              className="p-3 border rounded-xl bg-gray-50"
            >
              <strong>{player.name || `Player ${index + 1}`}</strong>
              <div className="text-sm text-gray-600">
                {player.race} {player.class} (Level {player.level})
              </div>
            </div>
          ))}
        </div>
      )}

      <div className="mt-6 italic text-gray-700">
        The Dungeon Master gathers their thoughtsâ€¦
      </div>
    </div>
  );
}
