import axios from "axios";

const API_URL = "http://localhost:3001/api";

// send player action
export async function playTurn({ message, campaign, party }) {
  const res = await axios.post(`${API_URL}/turn`, { message, campaign, party });
  return res.data;
}

// start a new campaign
export async function startCampaign({ theme, difficulty, party }) {
  const res = await axios.post(`${API_URL}/start-campaign`, { theme, difficulty, party });
  return res.data.campaignState;
}
