// frontend/src/state/campaignStore.js
import { create } from "zustand";

export const useCampaignStore = create((set) => ({
  campaign: null,       // theme, difficulty, etc.
  party: [],            // list of player characters
  lastResponse: "",     // last AI DM output
  voiceMode: true,      // whether AI speaks
  micListening: false,  // is mic listening

  // Start a new campaign
  startCampaign: ({ theme, difficulty, maxPlayers }) =>
    set({
      campaign: { theme, difficulty, maxPlayers, startedAt: new Date().toISOString() },
      party: [],
      lastResponse: "",
    }),

  // Add a player character
  addCharacter: (character) =>
    set((state) => ({
      party: [...state.party, character],
    })),

  // Update DM response and campaign state
  updateFromDM: (dmResponse) =>
    set((state) => ({
      lastResponse: dmResponse.aiResponse || "",
      campaign: { ...state.campaign, ...dmResponse.campaignState },
    })),

  toggleVoice: () => set((state) => ({ voiceMode: !state.voiceMode })),
  toggleMic: () => set((state) => ({ micListening: !state.micListening })),
}));
