  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0,0,0,0.8)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 2000,
      overflow: 'auto',
      padding: '20px'
    }}>
      <div style={{
        backgroundColor: '#1a1a1a',
        color: '#eee',
        padding: '30px',
        borderRadius: '15px',
        maxWidth: '900px',
        width: '100%',
        maxHeight: '90vh',
        overflowY: 'auto',
        border: '3px solid #ffd700',
        boxShadow: '0 0 30px rgba(255,215,0,0.3)'
      }}>
        {/* Header */}
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'start',
          marginBottom: '20px',
          paddingBottom: '20px',
          borderBottom: '2px solid #444'
        }}>
          <div>
            <h1 style={{ margin: 0, color: '#ffd700', fontSize: '32px' }}>
              {character.name}
            </h1>
            <p style={{ margin: '5px 0', color: '#aaa', fontSize: '16px' }}>
              Level {character.level} {character.race} {character.class}
            </p>
            {character.background && (
              <p style={{ margin: '5px 0', color: '#888', fontSize: '14px' }}>
                {character.background}
              </p>
            )}
          </div>
          <button
            onClick={onClose}
            style={{
              padding: '10px 20px',
              backgroundColor: '#444',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              fontSize: '16px'
            }}
