import { useState } from "react";
import CharacterSheetModal from "./CharacterSheetModal";
import { emptyCharacter } from "../../../shared/character.schema";

export default function DMSetup({ handleStartCampaign }) {
  const [tone, setTone] = useState("dark fantasy, eldritch horror");
  const [difficulty, setDifficulty] = useState("standard");
  const [playerCount, setPlayerCount] = useState(1);
  const [players, setPlayers] = useState([emptyCharacter]);
  const [editingIndex, setEditingIndex] = useState(null);

  const updatePlayerCount = (count) => {
    setPlayerCount(count);
    setPlayers(
      Array.from({ length: count }, (_, i) => players[i] ?? { ...emptyCharacter })
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleStartCampaign({ tone, difficulty, players });
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Start Your Campaign</h2>

      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <input value={tone} onChange={(e) => setTone(e.target.value)} />
        <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
          <option value="casual">Casual</option>
          <option value="standard">Standard</option>
          <option value="deadly">Deadly</option>
        </select>

        <label>Player Count</label>
        <select
          value={playerCount}
          onChange={(e) => updatePlayerCount(Number(e.target.value))}
        >
          {[1, 2, 3, 4, 5, 6].map((n) => (
            <option key={n} value={n}>{n}</option>
          ))}
        </select>

        <div className="grid grid-cols-2 gap-2">
          {players.map((p, i) => (
            <button
              type="button"
              key={i}
              onClick={() => setEditingIndex(i)}
              className="border p-2 rounded"
            >
              {p.name || `Player ${i + 1}`}
            </button>
          ))}
        </div>

        <button type="submit" className="bg-indigo-600 text-white p-2 rounded">
          Generate DM
        </button>
      </form>

      {editingIndex !== null && (
        <CharacterSheetModal
          player={players[editingIndex]}
          onSave={(updated) => {
            const copy = [...players];
            copy[editingIndex] = updated;
            setPlayers(copy);
            setEditingIndex(null);
          }}
          onClose={() => setEditingIndex(null)}
        />
      )}
    </div>
  );
}
