// Add to the top of campaignStore.js
import { 
  createQuest, 
  detectQuestEvents, 
  completeQuest,
  updateQuestObjective,
  addQuestNote,
  QUEST_STATUS 
} from '../utils/questSystem';

// Add to the store state (around line 5)
export const useCampaignStore = create((set) => ({
  campaign: null,
  party: [],
  lastResponse: "",
  quests: [], // ⭐ NEW: Quest tracker

  // ... existing state ...

  // ⭐ NEW: Quest management functions
  addQuest: (questData) =>
    set((state) => ({
      quests: [...state.quests, createQuest(questData)]
    })),

  updateQuest: (questId, updates) =>
    set((state) => ({
      quests: state.quests.map(q => 
        q.id === questId ? { ...q, ...updates } : q
      )
    })),

  completeQuestById: (questId) =>
    set((state) => ({
      quests: state.quests.map(q =>
        q.id === questId ? completeQuest(q) : q
      )
    })),

  addQuestNoteById: (questId, note) =>
    set((state) => ({
      quests: state.quests.map(q =>
        q.id === questId ? addQuestNote(q, note) : q
      )
    })),

  // ... rest of existing functions ...
}));
