// frontend/src/pages/PlayerList.jsx
import { useNavigate } from "react-router-dom";
import { useCampaignStore } from "../state/campaignStore";

export default function PlayerList() {
  const navigate = useNavigate();
  const { campaignState } = useCampaignStore();

  // If players array doesn't exist yet, create placeholders
  const players = campaignState?.players || [];

  const handleClick = (index) => {
    navigate(`/player/${index}`);
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Player Sheets</h2>

      {players.length === 0 ? (
        <p>No players added yet.</p>
      ) : (
        <ul className="space-y-2">
          {players.map((player, index) => (
            <li
              key={index}
              className="p-2 border rounded cursor-pointer hover:bg-indigo-100"
              onClick={() => handleClick(index)}
            >
              {player.name || `Player ${index + 1}`}
            </li>
          ))}
        </ul>
      )}

      <div className="mt-4">
        <button
          className="bg-indigo-600 text-white p-2 rounded hover:bg-indigo-700"
          onClick={() => navigate("/setup")}
        >
          Back to Campaign Setup
        </button>
      </div>
    </div>
  );
}
