// frontend/src/components/GameScreen.jsx

import { useState } from "react";
import { playTurn } from "../services/api";

export default function GameScreen({ systemPrompt, initialState }) {
  const [gameState, setGameState] = useState(initialState);
  const [narration, setNarration] = useState("");
  const [playerInput, setPlayerInput] = useState("");
  const [nextPrompt, setNextPrompt] = useState("What do you do?");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!playerInput) return;

    try {
      const response = await playTurn({ playerAction: playerInput, systemPrompt });
      setNarration(response.narration);
      setGameState(response.gameState);
      setNextPrompt(response.nextPrompt);
      setPlayerInput("");
    } catch (err) {
      console.error("Error during turn:", err);
      setNarration("The DM seems confused. Try again.");
    }
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <div className="bg-neutral-900 text-neutral-100 p-4 rounded-xl mb-4 min-h-[150px]">
        <p>{narration}</p>
      </div>
      <form onSubmit={handleSubmit} className="flex gap-2">
        <input
          value={playerInput}
          onChange={(e) => setPlayerInput(e.target.value)}
          className="flex-1 p-2 rounded-xl border"
          placeholder={nextPrompt}
        />
        <button type="submit" className="p-2 bg-indigo-600 text-white rounded-xl">
          Submit
        </button>
      </form>
    </div>
  );
}
