// frontend/src/components/DMSetup.jsx
import React, { useState } from "react";

export default function DMSetup({ onStart }) {
  const [tone, setTone] = useState("dark fantasy, eldritch horror");
  const [difficulty, setDifficulty] = useState("Standard");
  const [playerCount, setPlayerCount] = useState(1);
  const [playerSheets, setPlayerSheets] = useState([{ name: "", race: "", class: "", level: 1 }]);

  const handlePlayerCountChange = (count) => {
    const n = Math.max(1, parseInt(count));
    setPlayerCount(n);
    const sheets = [...playerSheets];
    while (sheets.length < n) sheets.push({ name: "", race: "", class: "", level: 1 });
    while (sheets.length > n) sheets.pop();
    setPlayerSheets(sheets);
  };

  const updateSheet = (index, field, value) => {
    const newSheets = [...playerSheets];
    newSheets[index][field] = value;
    setPlayerSheets(newSheets);
  };

  return (
    <div className="p-6 max-w-xl mx-auto bg-gray-900 text-white rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-4">Start Your Campaign</h2>

      <label>Campaign Tone / Theme:</label>
      <input
        value={tone}
        onChange={(e) => setTone(e.target.value)}
        className="w-full p-2 mb-4 rounded bg-gray-800"
      />

      <label>Difficulty:</label>
      <select
        value={difficulty}
        onChange={(e) => setDifficulty(e.target.value)}
        className="w-full p-2 mb-4 rounded bg-gray-800"
      >
        <option>Easy</option>
        <option>Standard</option>
        <option>Hard</option>
      </select>

      <label>Player Count:</label>
      <input
        type="number"
        min="1"
        max="6"
        value={playerCount}
        onChange={(e) => handlePlayerCountChange(e.target.value)}
        className="w-full p-2 mb-4 rounded bg-gray-800"
      />

      {playerSheets.map((sheet, idx) => (
        <div key={idx} className="mb-4 p-2 bg-gray-800 rounded">
          <h4 className="font-bold mb-1">Player {idx + 1}</h4>
          <input
            placeholder="Name"
            value={sheet.name}
            onChange={(e) => updateSheet(idx, "name", e.target.value)}
            className="w-full p-1 mb-1 rounded bg-gray-700"
          />
          <input
            placeholder="Race"
            value={sheet.race}
            onChange={(e) => updateSheet(idx, "race", e.target.value)}
            className="w-full p-1 mb-1 rounded bg-gray-700"
          />
          <input
            placeholder="Class"
            value={sheet.class}
            onChange={(e) => updateSheet(idx, "class", e.target.value)}
            className="w-full p-1 mb-1 rounded bg-gray-700"
          />
          <input
            type="number"
            min="1"
            placeholder="Level"
            value={sheet.level}
            onChange={(e) => updateSheet(idx, "level", e.target.value)}
            className="w-full p-1 rounded bg-gray-700"
          />
        </div>
      ))}

      <button
        className="w-full bg-yellow-500 hover:bg-yellow-600 p-3 rounded font-bold"
        onClick={() => onStart({ tone, difficulty, playerSheets })}
      >
        Generate DM
      </button>
    </div>
  );
}
