// frontend/src/components/DMSetup.jsx
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCampaignStore } from "../state/campaignStore";
import { startCampaign } from "../services/api";
import { musicManager } from "../utils/musicSystem";
import CharacterCreator from "./CharacterCreator";

export default function DMSetup() {
  const [theme, setTheme] = useState("Dark Fantasy");
  const [difficulty, setDifficulty] = useState("Normal");
  const [party, setParty] = useState([]);
  const [showCharacterCreator, setShowCharacterCreator] = useState(false);
  const { setCampaign } = useCampaignStore();
  const navigate = useNavigate();

  // üéµ Play main menu music on mount
  useEffect(() => {
    musicManager.play('main_menu');

    return () => {
      // Don't stop music on unmount - it will transition to game music
    };
  }, []);

  const handleAddCharacter = (character) => {
    setParty([...party, character]);
    setShowCharacterCreator(false);
  };

  const handleRemoveCharacter = (index) => {
    setParty(party.filter((_, i) => i !== index));
  };

  const handleStart = async () => {
    if (party.length === 0) {
      alert("Please create at least one character!");
      return;
    }

    console.log("Starting campaign...");
    try {
      const campaignState = await startCampaign({ theme, difficulty, party });
      console.log("Backend returned:", campaignState);

      setCampaign(campaignState);
      navigate("/play");
    } catch (err) {
      console.error("Failed to start campaign:", err);
      alert("Failed to start campaign. Check console for details.");
    }
  };

  return (
    <div className="container">
      {/* Hero Header */}
      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <h1 style={{
          fontSize: '48px',
          margin: '40px 0 10px 0',
          textShadow: '0 0 20px rgba(255,215,0,0.5)'
        }}>
          ‚öîÔ∏è AI Dungeon Master
        </h1>
        <p className="text-secondary" style={{ fontSize: '18px' }}>
          Create your adventure and forge your heroes
        </p>
      </div>

      {/* Campaign Settings */}
      <div className="card-elevated mb-xl">
        <h2 className="text-primary" style={{ marginTop: 0 }}>
          ‚öôÔ∏è Campaign Settings
        </h2>

        <div className="mb-lg">
          <label>üé≠ Theme:</label>
          <input
            value={theme}
            onChange={(e) => setTheme(e.target.value)}
            className="input"
            placeholder="e.g., Dark Fantasy, Sci-Fi, Horror..."
          />
        </div>

        <div>
          <label>‚ö° Difficulty:</label>
          <select
            value={difficulty}
            onChange={(e) => setDifficulty(e.target.value)}
            className="input"
            style={{ cursor: 'pointer' }}
          >
            <option>Easy</option>
            <option>Normal</option>
            <option>Hard</option>
            <option>Deadly</option>
          </select>
        </div>
      </div>

      {/* Party */}
      <div className="card-elevated mb-xl">
        <div className="flex flex-between mb-lg">
          <h2 className="text-primary" style={{ margin: 0 }}>
            üë• Your Party
          </h2>
          <button
            onClick={() => setShowCharacterCreator(true)}
            className="btn btn-success"
          >
            ‚ûï Create Character
          </button>
        </div>

        {party.length === 0 ? (
          <div style={{
            textAlign: 'center',
            padding: '40px 20px'
          }}>
            <p className="text-muted" style={{ 
              fontSize: '18px', 
              margin: 0,
              fontStyle: 'italic'
            }}>
              No heroes yet. Create your first character to begin!
            </p>
          </div>
        ) : (
          <div className="grid grid-auto">
            {party.map((character, index) => (
              <div key={index} className="card card-hover" style={{ position: 'relative' }}>
                <button
                  onClick={() => handleRemoveCharacter(index)}
                  className="btn btn-danger btn-sm"
                  style={{
                    position: "absolute",
                    top: "10px",
                    right: "10px",
                    width: "30px",
                    height: "30px",
                    borderRadius: '50%',
                    padding: 0,
                    fontSize: '18px',
                    lineHeight: '30px'
                  }}
                  title="Remove character"
                >
                  ‚úï
                </button>

                <h3 className="text-primary" style={{
                  margin: "0 0 10px 0",
                  fontSize: "24px",
                  paddingRight: '40px'
                }}>
                  {character.name}
                </h3>

                <p className="text-secondary" style={{ margin: "5px 0" }}>
                  Level {character.level} {character.race} {character.class}
                </p>

                {character.background && (
                  <p className="text-muted" style={{
                    margin: "10px 0",
                    fontSize: "14px",
                    fontStyle: "italic",
                    borderTop: "1px solid #444",
                    paddingTop: "10px"
                  }}>
                    "{character.background.substring(0, 100)}{character.background.length > 100 ? '...' : ''}"
                  </p>
                )}

                <div className="flex" style={{
                  marginTop: "15px",
                  justifyContent: "space-around",
                  paddingTop: "15px",
                  borderTop: "1px solid #444"
                }}>
                  {Object.entries(character.abilities).slice(0, 3).map(([ability, score]) => (
                    <div key={ability} style={{ textAlign: "center" }}>
                      <div className="text-muted" style={{ fontSize: "12px" }}>
                        {ability}
                      </div>
                      <div className="text-primary" style={{ 
                        fontSize: "20px", 
                        fontWeight: "bold" 
                      }}>
                        {score}
                      </div>
                    </div>
                  ))}
                </div>

                <div className="text-secondary" style={{
                  marginTop: "15px",
                  fontSize: "14px"
                }}>
                  ‚ù§Ô∏è HP: {character.maxHp} | üõ°Ô∏è AC: {character.armorClass}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Start Button */}
      <button
        onClick={handleStart}
        disabled={party.length === 0}
        className={party.length > 0 ? 'btn btn-primary btn-lg glow' : 'btn btn-ghost btn-lg'}
        style={{
          width: "100%",
          fontSize: "24px"
        }}
      >
        {party.length === 0 ? "‚ö†Ô∏è Create characters to begin" : "üéÆ Begin Adventure!"}
      </button>

      {/* Character Creator Modal */}
      {showCharacterCreator && (
        <CharacterCreator
          onComplete={handleAddCharacter}
          onCancel={() => setShowCharacterCreator(false)}
        />
      )}
    </div>
  );
}
