// frontend/src/components/CombatTracker.jsx
import { useState } from 'react';
import { 
  executeAttack, 
  nextTurn, 
  checkCombatEnd,
  getCurrentCombatant,
  updateCombatant,
  applyDamage
} from '../utils/combatSystem';

export default function CombatTracker({ combat, onUpdate, onEnd }) {
  const [selectedTarget, setSelectedTarget] = useState(null);
  const [combatLog, setCombatLog] = useState([]);

  if (!combat || !combat.isActive) return null;

  const currentCombatant = getCurrentCombatant(combat);
  const isPlayerTurn = !currentCombatant.isEnemy;

  const handleAttack = (attackIndex = 0) => {
    if (!selectedTarget) {
      alert('Select a target first!');
      return;
    }

    const result = executeAttack(currentCombatant, selectedTarget, attackIndex);

    const logEntry = result.attackRoll.isCritical ? 
      `üí• CRITICAL HIT! ${result.attacker} hits ${result.target} with ${result.attack} for ${result.damage} ${result.damageType} damage!` :
      result.attackRoll.isFumble ?
      `‚ùå FUMBLE! ${result.attacker} misses ${result.target} with ${result.attack}!` :
      result.attackRoll.isHit ?
      `‚öîÔ∏è ${result.attacker} hits ${result.target} with ${result.attack} for ${result.damage} ${result.damageType} damage!` :
      `üõ°Ô∏è ${result.attacker} misses ${result.target} with ${result.attack}!`;

    setCombatLog([logEntry, ...combatLog]);

    if (result.attackRoll.isHit) {
      const damagedTarget = applyDamage(selectedTarget, result.damage);
      const updatedCombat = updateCombatant(combat, selectedTarget.id || selectedTarget.name, damagedTarget);

      if (damagedTarget.isDead) {
        setCombatLog([`üíÄ ${damagedTarget.name} has been defeated!`, ...combatLog]);
      }

      onUpdate(updatedCombat);

      const endCheck = checkCombatEnd(updatedCombat);
      if (endCheck.ended) {
        setTimeout(() => onEnd(endCheck), 1000);
        return;
      }
    }

    handleEndTurn();
  };

  const handleEndTurn = () => {
    setSelectedTarget(null);
    const newCombat = nextTurn(combat);
    onUpdate(newCombat);

    setTimeout(() => {
      if (newCombat.combatants[newCombat.currentTurn]?.isEnemy) {
        handleEnemyTurn(newCombat);
      }
    }, 500);
  };

  const handleEnemyTurn = (currentCombat) => {
    const enemy = getCurrentCombatant(currentCombat);
    if (enemy.isDead) {
      handleEndTurn();
      return;
    }

    const aliveParty = currentCombat.combatants.filter(c => !c.isEnemy && !c.isDead);
    if (aliveParty.length === 0) return;

    const target = aliveParty[Math.floor(Math.random() * aliveParty.length)];
    const result = executeAttack(enemy, target, 0);

    const logEntry = result.attackRoll.isCritical ? 
      `üí• CRITICAL! ${result.attacker} critically hits ${result.target} for ${result.damage} damage!` :
      result.attackRoll.isHit ?
      `‚öîÔ∏è ${result.attacker} hits ${result.target} for ${result.damage} damage!` :
      `üõ°Ô∏è ${result.attacker} misses ${result.target}!`;

    setCombatLog([logEntry, ...combatLog]);

    if (result.attackRoll.isHit) {
      const damagedTarget = applyDamage(target, result.damage);
      let updatedCombat = updateCombatant(currentCombat, target.id || target.name, damagedTarget);

      if (damagedTarget.isDead) {
        setCombatLog([`üíÄ ${damagedTarget.name} has fallen!`, ...combatLog]);
      }

      onUpdate(updatedCombat);

      const endCheck = checkCombatEnd(updatedCombat);
      if (endCheck.ended) {
        setTimeout(() => onEnd(endCheck), 1000);
        return;
      }
    }

    setTimeout(() => handleEndTurn(), 1500);
  };

  return (
    <div style={{
      position: 'fixed',
      bottom: 0,
      left: 0,
      right: 0,
      backgroundColor: '#1a1a1a',
      border: '3px solid #d32f2f',
      borderBottom: 'none',
      padding: '20px',
      maxHeight: '60vh',
      overflowY: 'auto',
      zIndex: 1000,
      boxShadow: '0 -4px 20px rgba(211,47,47,0.4)'
    }}>
      {/* Header */}
      <div className="flex flex-between mb-lg" style={{ paddingBottom: '15px', borderBottom: '2px solid #444' }}>
        <h2 className="text-danger" style={{ margin: 0 }}>
          ‚öîÔ∏è Combat - Round {combat.round}
        </h2>
        <button
          onClick={() => onEnd({ ended: true, result: 'flee', message: 'Combat ended' })}
          className="btn btn-ghost btn-sm"
        >
          End Combat
        </button>
      </div>

      {/* Initiative Order */}
      <div className="mb-lg" style={{ display: 'flex', gap: '10px', overflowX: 'auto', paddingBottom: '10px' }}>
        {combat.combatants.map((combatant, index) => (
          <div
            key={combatant.id || combatant.name}
            className={index === combat.currentTurn ? 'card-active' : 'card'}
            style={{
              minWidth: '120px',
              padding: '10px',
              opacity: combatant.isDead ? 0.3 : 1,
              position: 'relative'
            }}
          >
            {index === combat.currentTurn && (
              <div style={{
                position: 'absolute',
                top: '-15px',
                left: '50%',
                transform: 'translateX(-50%)',
                fontSize: '24px'
              }}>
                ‚¨áÔ∏è
              </div>
            )}

            <div style={{
              fontSize: '12px',
              color: combatant.isEnemy ? '#ff6b6b' : '#4CAF50',
              marginBottom: '5px'
            }}>
              {combatant.isEnemy ? 'üëπ' : 'üõ°Ô∏è'} Init: {combatant.initiative}
            </div>

            <div style={{ fontWeight: 'bold', marginBottom: '5px', fontSize: '14px' }}>
              {combatant.name}
            </div>

            <div style={{ fontSize: '12px', marginBottom: '5px' }}>
              HP: {combatant.hp}/{combatant.maxHp}
            </div>

            <div className="progress-bar">
              <div 
                className="progress-fill"
                style={{
                  width: `${(combatant.hp / combatant.maxHp) * 100}%`,
                  backgroundColor: combatant.hp > combatant.maxHp * 0.5 ? '#4CAF50' : 
                                  combatant.hp > combatant.maxHp * 0.25 ? '#ff9800' : '#f44336'
                }}
              />
            </div>

            {combatant.isDead && (
              <div style={{
                position: 'absolute',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '40px'
              }}>
                üíÄ
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Combat Log */}
      <div className="card mb-lg" style={{
        backgroundColor: '#0a0a0a',
        maxHeight: '100px',
        overflowY: 'auto'
      }}>
        {combatLog.length === 0 ? (
          <p className="text-muted" style={{ margin: 0, fontStyle: 'italic' }}>
            Combat begins...
          </p>
        ) : (
          combatLog.map((log, i) => (
            <div key={i} style={{
              padding: '5px',
              marginBottom: '3px',
              fontSize: '14px'
            }}>
              {log}
            </div>
          ))
        )}
      </div>

      {/* Current Turn Actions */}
      <div className="card-active">
        <h3 className="text-primary" style={{ margin: '0 0 15px 0' }}>
          {currentCombatant.name}'s Turn
        </h3>

        {isPlayerTurn && !currentCombatant.isDead && (
          <div>
            {/* Target Selection */}
            <div className="mb-md">
              <label style={{ fontWeight: 'bold', marginBottom: '8px' }}>
                Select Target:
              </label>
              <div className="flex gap-sm" style={{ flexWrap: 'wrap' }}>
                {combat.combatants.filter(c => c.isEnemy && !c.isDead).map(enemy => (
                  <button
                    key={enemy.id || enemy.name}
                    onClick={() => setSelectedTarget(enemy)}
                    className={selectedTarget?.name === enemy.name ? 'btn btn-danger btn-sm' : 'btn btn-ghost btn-sm'}
                  >
                    {enemy.name} ({enemy.hp}/{enemy.maxHp})
                  </button>
                ))}
              </div>
            </div>

            {/* Attack Buttons */}
            {currentCombatant.attacks && currentCombatant.attacks.length > 0 && (
              <div className="mb-md">
                <label style={{ fontWeight: 'bold', marginBottom: '8px' }}>
                  Attack:
                </label>
                <div className="flex gap-sm">
                  {currentCombatant.attacks.map((attack, index) => (
                    <button
                      key={index}
                      onClick={() => handleAttack(index)}
                      disabled={!selectedTarget}
                      className="btn btn-success"
                    >
                      ‚öîÔ∏è {attack.name} (+{attack.bonus}, {attack.damage})
                    </button>
                  ))}
                </div>
              </div>
            )}

            <button onClick={handleEndTurn} className="btn btn-ghost">
              End Turn
            </button>
          </div>
        )}

        {!isPlayerTurn && (
          <p className="text-danger" style={{ margin: 0, fontStyle: 'italic' }}>
            Enemy is thinking...
          </p>
        )}

        {currentCombatant.isDead && (
          <p className="text-muted" style={{ margin: 0 }}>
            This combatant is defeated.
          </p>
        )}
      </div>
    </div>
  );
}
