// frontend/src/App.jsx

import { useState } from "react";
import DMSetup from "./components/DMSetup";
import GameScreen from "./components/GameScreen";

export default function App() {
  const [campaignStarted, setCampaignStarted] = useState(false);
  const [systemPrompt, setSystemPrompt] = useState("");
  const [gameState, setGameState] = useState(null);
  const [narration, setNarration] = useState("");

  // Called by DMSetup when campaign starts
  const handleStartCampaign = ({ tone, difficulty }) => {
    import("./services/api").then(({ startCampaign }) => {
      startCampaign({ tone, difficulty })
        .then((result) => {
          setSystemPrompt(result.systemPrompt);
          setGameState(result.gameState);
          setNarration(result.narration);
          setCampaignStarted(true);
        })
        .catch((err) => console.error("Error starting campaign:", err));
    });
  };

  return (
    <div className="min-h-screen bg-neutral-800 text-white">
      {!campaignStarted ? (
        <DMSetup handleStartCampaign={handleStartCampaign} />
      ) : (
        <GameScreen systemPrompt={systemPrompt} initialState={gameState} />
      )}
    </div>
  );
}
