import axios from "axios";

const BASE_URL = "http://localhost:3001";

export async function startCampaign({ theme, difficulty, party }) {
  const response = await axios.post(`${BASE_URL}/start-campaign`, {
    theme,
    difficulty,
    party,
  });
  return response.data.campaignState;
}

export async function playTurn({ message, campaign, party }) {
  const response = await axios.post(`${BASE_URL}/turn`, {
    message,
    campaign,
    party,
  });
  return response.data;
}

export async function speakText({ text, voice }) {
  const response = await axios.post(`${BASE_URL}/voice`, {
    text,
    voice,
  });
  return response.data.audio;
}
