import { useNavigate } from "react-router-dom";

export default function PlayerList({ playerCount, playerSheets, setPlayerSheets }) {
  const navigate = useNavigate();

  const handleClick = (index) => {
    navigate(`/player/${index + 1}`);
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Player Sheets</h2>
      <ul className="flex flex-col gap-2">
        {Array.from({ length: playerCount }).map((_, i) => (
          <li
            key={i}
            onClick={() => handleClick(i)}
            className="p-2 border rounded-xl cursor-pointer hover:bg-gray-200"
          >
            Player {i + 1} Sheet
          </li>
        ))}
      </ul>
    </div>
  );
}
