import React, { useState } from "react";
import axios from "axios";

export default function GameScreen() {
  // ✅ Campaign state
  const [campaignConfig, setCampaignConfig] = useState(null);
  const [gameState, setGameState] = useState({});
  const [playerAction, setPlayerAction] = useState("");
  const [aiResponse, setAiResponse] = useState("");

  // Start a new campaign
  const handleStartCampaign = () => {
    const config = {
      name: "Epic AI Dungeon",
      difficulty: "Hard",
      startedAt: new Date().toISOString(),
    };
    setCampaignConfig(config);
    setGameState({}); // Reset game state
    setAiResponse("");
  };

  // Handle submitting a player action
  const handlePlayTurn = async () => {
    if (!playerAction) return;

    try {
      const response = await axios.post(
        "http://localhost:3001/api/turn/play",
        {
          playerAction,
          gameState,
        }
      );

      // Update state with backend response
      setGameState(response.data.gameState);
      setAiResponse(response.data.aiResponse);
      setPlayerAction(""); // Clear input
    } catch (error) {
      console.error("Error playing turn:", error);
      setAiResponse("⚠️ Failed to play turn. Check console for details.");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif" }}>
      {!campaignConfig ? (
        <button onClick={handleStartCampaign}>Start New Campaign</button>
      ) : (
        <>
          <h2>{campaignConfig.name}</h2>
          <p>
            <strong>Difficulty:</strong> {campaignConfig.difficulty}
          </p>
          <p>
            <strong>AI Response:</strong> {aiResponse}
          </p>

          <input
            type="text"
            placeholder="Enter your action..."
            value={playerAction}
            onChange={(e) => setPlayerAction(e.target.value)}
            style={{ width: "300px", marginRight: "10px" }}
          />
          <button onClick={handlePlayTurn}>Submit Action</button>
        </>
      )}
    </div>
  );
}
